css:
@-moz-document domain("ishtar-collective.net") {
html, body, .content {
    background-color: #4f4f4f;
    color: #cccccc;
}

.sellify-countdown-timer-holder,
input, select, textarea 
.page-search .form-group button, .page-errors .form-group button,

.main .field__input,
.content-box, .content-box__row--secondary
{
    background: #484848;
}

body, h1, .h1, h2, .h2, h3, .h3, h4, .h4, h5, .h5, h6, .h6,
body, input, textarea, button, select,
.left-column-section,
.sub-footer a, .site-footer a, .sub-footer, .site-footer,
.top-bar a, .site-header a, .site-nav__link, .site-nav--active a, .site-nav__submenu .site-nav--active a, .product__title a, .collection__title a,
.sellify-countdown-timer-holder,
.breadcrumb-nav *, .table--no-border td, .table--no-border th, .product-single__quantity input, .quantity-selector, .btn--secondary,

.anyflexbox .main *, .sidebar, .sidebar .order-summary__emphasis, .sidebar .payment-due__price, .sidebar .payment-due-label__total
{
  color: #ccc;
}

a,
.sellify-countdown-timer-holder a
{
    text-decoration: none;
    color: #1ad0d0;
}

.header input{
    color: #999999;
    border-bottom: 1px solid rgba(166, 172, 172, .3);
    
    -webkit-filter: brightness(5);
    filter: brightness(5);
    
    /*filter: invert(50%);*/
}

.header input:focus, .header input:valid,
.site-nav__element:first-child, hr
{
    border-color: #ccc;
}


.page-search .form-group input, .page-errors .form-group input {
    color: #c2c2c2;
}

.dark-header {
    background-color: #484848;
}
.page-grimoire_cards-show .header-background {
    background-color: #484848;
}

.description a {
    text-decoration: none;
}


.page-grimoire_cards-show .description {
    float: left;
    color: #cccccc;
    padding-top: 25px;
}

.drop-caps:first-letter {
    border: 6px solid #cccccc;
}


.color-confidence-1 {
    background-color: #666666;
}
.color-confidence-2 {
    background-color: #578685;
}
.color-confidence-3 {
    background-color: #6e9574;
}
.icon-confidence-1, .page-categories-show [class*='-details'] {
    color: #cccccc;
}
.page-categories-show .icon-confidence {
    margin-bottom: 10px;
    color: #cccccc;
}

.item-details .item-description {
    color: inherit;
}

.item-details .item-display-source {
    color: inherit;
}
.page-grimoire_cards-show .intro-text {
    color: #686868;
    font-size: 20px;
}

.sidebar
{
  padding: 10px;
}
}
