css:
@-moz-document domain("translate.googleusercontent.com"), domain("translate.google.com"), domain("translate.google.com.au"), url-prefix(http://), url-prefix(https://) {

#wtgbr, #gt-c, .SPRITE_close,
#google-infowindow > div:nth-child(1) > div:nth-child(21) > div:nth-child(1) > div:nth-child(2),
#google-infowindow > div:nth-child(1) > div:nth-child(21) > div:nth-child(1) > div:nth-child(1) > img:nth-child(1),
#google-infowindow > div:nth-child(1) > div:nth-child(21) > div:nth-child(1) > div:nth-child(4) > div:nth-child(1),
#google-infowindow > div > img, div.gmnoprint img, 
.SPRITE_close, .SPRITE_maximize, .SPRITE_restore, 
.SPRITE_iw_nw, .SPRITE_iw_ne, .SPRITE_iw_sw0, .SPRITE_iw_se0, 
.SPRITE_iws_nw, .SPRITE_iws_ne, .SPRITE_iws_sw, .SPRITE_iws_se, 
.SPRITE_iw_xtap, .SPRITE_iw_xtap_l, .SPRITE_iw_xtap_u, .SPRITE_iw_xtap_ul, .SPRITE_iw_xtap_rd, .SPRITE_iw_xtap_ld, 
.SPRITE_iws_tap, .SPRITE_iws_tap_l, .SPRITE_iws_tap_u, .SPRITE_iws_tap_ul, .SPRITE_iws_tap_rd, .SPRITE_iws_tap_ld, 
.SPRITE_iws_tab_dl, .SPRITE_iws_tab_dr, .SPRITE_iws_tab_l, .SPRITE_iws_tab_r {
    display: none !important;
}

#gt-c {
    width: 99% !important;
    min-width: 99% !important;
}

#google-infowindow > div:nth-child(1) > div:nth-child(21) > div:nth-child(1) > div:nth-child(1) {
    margin: 0px !important;
    padding: 0px !important;
}




/*  Add-ons  */

#google-infowindow > div:nth-child(1) {
    z-index: 20000 !important;                   /*  Lowers Google Translate pop-up overlay priority.  */
}

#rikaichan-window, #rikaichamp-window {
    z-index: 9888777666 !important;              /*  Prevents translation add-on pop-up displaying under Google Translate pop-up.  */
}

#cperapera-window { 
    z-index: 9888777666 !important;              /*  Prevents translation add-on pop-up displaying under Google Translate pop-up.  */
}

#zhongwen-window {
    border-radius: 0px;
    box-shadow: none;
}




/*  Google Translate (GT)  */

#contentframe {
    top: 0px !important;                         /*  Header bar > remove  */
}

.google-src-active-text a, .google-src-active-text a:link {
    color: #00aeffe6 !important;                 /*  Popup window > hyperlink color  */
}

.google-src-active-text {
    padding: 10px !important;                    /*  Popup window > spacing  */
}

#google-infowindow > div:nth-child(1) > div:nth-child(21) {
    height: auto !important;                     /*  Popup window > size  */
}

.google-src-active-text, .google-src-active-text a, .google-src-active-text a:link {
    font-size: 25px !important;                  /*  Popup window > text  */
    line-height: 1.1 !important;
}




/*  GT pop-up color scheme  */

.google-src-active-text {
    color: #FFFFFF !important;
    border-color: #FFFFFF;
    border-width: 1px;
    border-style: solid;
}

#google-infowindow > div:nth-child(1) > div:nth-child(21) {
    background-color: #333333 !important;
}








}
