css:
@-moz-document url-prefix(https://reppeat.com) {
    /*
     * Youtube Reppeat Dark Theme (2019).
     * 
     * CC BY - Creative Commons Attribution
     */

    html, body, .home-section {
        color: #d0d0d0;
        background: #222222;
    }

    .header, .drawer, .card, .sidebar {
        color: #d0d0d0;
        background: #333333;
    }

    /* Header */
    .header {
        box-shadow: 0 1px 0 #000000;
    }

    .search_bar {
        background: #333333;
        border: #888888 solid 1px;
        -webkit-box-shadow: 0 0 3px #000000;
        box-shadow: 0 0 3px #000000;
    }

    .search_bar input[type=text] {
        background: rgba(255, 255, 255, 0.13);
    }

    .search_bar button[type=submit] {
        background-color: rgba(255, 255, 255, 0.13);
        color: rgba(255, 255, 255, 0);
        border: 1px solid #888888;
        transition: color .1s ease-in-out;
    }

    .search_bar button[type=submit]:hover, .search_bar button[type=submit]:focus {
        background-color: rgba(0, 0, 0, 0.13);
    }


    /* Drawer */
    .drawer--active .drawer--container {
        box-shadow: 1px 0 0 #000000;
    }

    .drawer__list li:hover {
        background-color: rgba(0, 0, 0, 0.13);
    }

    input[type=text] {
        background: #2C2C2C;
    }

    /* Video */
    #slider .ui-slider-handle::before, #slider .ui-slider-handle::after {
        color: #ffffff;
    }

    /* Sections */
    .search__container {
        color: #d0d0d0;
        background: #2C2C2C;
    }

    .card--description .card__content::after {
        background: none;
    }

    .card__footer {
        border-top: 1px solid #888888;
    }

    .hover {
        background: rgba(0, 0, 0, 0.13) none repeat scroll 0% 0%;
        -webkit-box-shadow: 2px 2px 3px 1px rgba(0, 0, 0, 0.06);
        -moz-box-shadow: 2px 2px 3px 1px rgba(0, 0, 0, 0.06);
        box-shadow: 2px 2px 3px 1px rgba(0, 0, 0, 0.06);
    }

    .sidebar__title {
        color: rgba(255,255,255,.87);
        border-bottom: 1px solid #888888;
    }

    .button {
        color: #fff;
        background: rgba(0, 0, 0, 0.13);
    }

    .button:hover, .btn--icon-dots:hover, .btn--icon-plus:hover {
        background: rgba(0, 0, 0, 0.26);
    }

    .menu {
        color: #d0d0d0;
        background: #2C2C2C;
    }

    .menu__title {
        color: #ffffff;
    }

    .read-more a {
        color: #ffffff;
    }

    a {
        color: #e14e42;
    }

    a:hover, :focus {
        color: rgb(225, 78, 66, 0.66);
    }

    th {
        color: rgb(225, 78, 66, 0.66);
    }

    /* Footer */
    .footer__link {
        color: #fcfcfc;
    }
}
