css:
@-moz-document domain("dicecloud.com") {
body {
	background-color: #2F2F2F;
}

.card, .stat-card, .ability-mini-card, .item, .white, paper-icon-item {
	background-color: #424242 !important;
	color: #EAEAEA !important;
}

.dialog {
	background-color: #424242 !important;
	line-height: 1.2;
}

.form.flex.scroll-y {
	line-height: 1.5;
}

.yellow, .amber, .orange, .lime, .grey {
	color: #2F2F2F;
	font-weight: 500;
}

.item {
	margin: 4px 0 4px 0;
}

.input-content ::content input, input, .item iron-icon, .item paper-icon-button, .black54, .slotBubble, editing-view-port {
	color: #EAEAEA;
}

.slotBubble[disabled] {
	color: #b3b0b0
}

.tab-page.fit, .card .bottom.list .paper-font-subhead, textarea {
	color: #EAEAEA !important;
}

paper-button {
	background: rgba(255, 255, 255, .1);
	color: rgba(255, 255, 255, .87);
}

.form.flex.scroll-y {
	filter: invert(100%);
}

.form .horizontal paper-button {
	background: rgba(0, 0, 0, .1);
	color: rgba(0, 0, 0, .87);
}

.form .layout paper-button:disabled {
	background: #151515 !important;
	color: #575757 !important;
}

hr {
	border-color: #151515;
}

.form.flex.scroll-y iron-image, .fit .feedback, iframe {
	filter: invert(100%);
}
}

@-moz-document regexp(".+?dicecloud.com\\/character\\/.+?\\/json.+") {
body {
	background: #2F2F2F;
	filter: invert(100%);
}
}
