css:
@-moz-document url-prefix("https://www.eff.org") {
/*Dark Stuff*/
/*dark-dark*/
body, input, #footer-bottom, .long-read .pane-node-field-issue a:hover, .more-link a:hover{
    background-color: #292929 !important;
    color: #d5d5d5;
}
/*light-dark*/
.newsletter-form, .long-read .pane-node-field-issue a, #footer, .more-link a, .ui-selectmenu-button, .ui-widget-content, .ui-menu-item, #badger-description-download {
    background-color: #2f2f2f !important;
    color: #d5d5d5;
}
/*top bar*/
#topbar {
    background: linear-gradient(to top, #292929 30%,#323232 70%);
}

/*text*/
/*text general*/
.field-content, input, .node-author, .node-date, .lefthand-sidebar-sidebar .menu li.opened > a.active-trail, .pane-title, .date, .effector-desc, form input[type="text"]::placeholder, form input[type="email"]::placeholder, .take-explainer {
    color: #d5d5d5 !important;
    background-image: none !important;
}
/*blocks & codeboxes*/
blockquote, .byline{
    border-color: #ec1e1e !important;
    color: #d5d5d5 !important;
}
/*red stuff*/
a, a:hover, .eff-tagline, .effector-title {
    color: #ec1e1e !important;
    background-color: transparent !important;
}

/*Miscelaneous*/
/*search icon*/
#masthead #main-menu-nav form.search-site button[type="submit"] {
    background: url(https://www.eff.org/sites/all/themes/phoenix/images/search-icon-white.svg) no-repeat;
    background-size: 200%;
    background-position-x: 60%;
    background-position-y: 60%;
}
/*remove share buttons*/
.share-links {
    display: none !important;
}
/*pager*/
.pager .pager__item--current {
    border-color: #ec1e1e !important;
    color: #d5d5d5 !important;
    background-color: #2f2f2f !important;
}
}
