css:
@-moz-document domain(betsdota2.com) {
  .grid-inner__sidebar,
  .sidebar__section,
  .chat-place,
  .layout__footer,
  .header__bottom .wrapper,
    .bet-match__rating-percents{
      display: none !important;
  }
  .bet-item.bet-left .bet-match .bet-match__user-bet_left {
      left: 0;
  }
  .bet-item.bet-right .bet-match .bet-match__user-bet_right {
      right: 0;
  }
  .grid-inner__main {
      padding: 0
  }
  .logo {
      width: 100px!important;
      height: 50px!important;
  }
  .logo::before {
      display: none!important
  }
  .logo .logo__text {
      display: block!important;
      padding: 0!important;
      margin: 0!important;
      color: black!important;
      font-size: 16px!important;
      font-weight: bold!important;
  }
  .header__projects {
      transform: none!important;
  }
  .grid-inner {
      max-width: 1000px;
      margin: 0 auto;
  }
  @media (min-width: 970px) {
      .bet-match__picture-wrapper {
          display: none;
      }
  }
  body {
      background: #444!important;
      color: #000!important;
  }
  .grid-inner {
      background: rgba(200, 200, 200, 1);
      padding: 5px;
  }
  .bet-match {
      background: rgba(255, 255, 255, 0.75);
  }
  .bet-event {
      background: rgba(255, 255, 255, 0.66);
  }
.bm-additional:before{
   background: linear-gradient(to right, rgba(255, 255, 255, .0) 0%, rgba(255, 255, 255, .66) 20%, rgba(255, 255, 255, 0.66) 80%, rgba(255, 255, 255, .0) 100%)
}
  .teams-comparison,
  .teamstats {
      background: rgb(63, 63, 78);
      color: #fff;
  }
  .teams-comparison__center {
      display: none;
  }
  .teamstats .lm-opponent,
  .teamstats .lm-score,
  .teamstats .lm-date,
  .lmvs-date {
      color: #fff!important;
  }
  @media (min-width: 970px) {
      .bet-match__controls {
          right: -130px;
          width: 0;
      }
      .bet-match__info {
          width: 100%;
      }
  }
}
