css:
@-moz-document domain("lnmtl.com") {

* :not(select):not(:hover)>.navbar-fixed-top:not(:hover),* > .navbar-fixed-top:not(:hover)
    {
    opacity: 0 !important;
    transition: all .5s !important;
    }

#scrollUp, .footer-right, .cc-window, .footer, div.dashhead-toolbar-item:nth-child(1), .dashhead-toolbar-divider, 
#app > widgetbot-crate:nth-child(15), .root, #topNav
    {
    display: none !important;
    }

.container, .jumbotron .container, sentence.original:nth-child(122), #table, .col-sm-12, ul.clearfix
    {
    width: 100% !important;
    }

body, .container, .jumbotron, .chapter-head, .dashhead-subtitle, .odd > td:nth-child(5), .row,
h3, .popover, .popover-title, .popover-content, .navbar-default, .navbar-nav, tr.odd > td, tr.even > td,
.col-lg-1, .col-lg-2, .col-lg-3, .col-lg-4, .col-lg-5, .col-lg-6, .col-lg-7, .col-lg-8, .col-lg-9, .col-lg-10, .col-lg-11, 
.col-lg-12, .col-md-1, .col-md-2, .col-md-3, .col-md-4, .col-md-5, .col-md-6, .col-md-7, .col-md-8, .col-md-9, .col-md-10, 
.col-md-11, .col-md-12, .col-sm-1, .col-sm-2, .col-sm-3, .col-sm-4, .col-sm-5, .col-sm-6, .col-sm-7, .col-sm-8, .col-sm-9, 
.col-sm-10, .col-sm-11, .col-sm-12, .col-xs-1, .col-xs-2, .col-xs-3, .col-xs-4, .col-xs-5, .col-xs-6, .col-xs-7, .col-xs-8, 
.col-xs-9, .col-xs-10, .col-xs-11, .col-xs-12
    {
    margin: 0px !important;
    padding: 0px !important;
    }

.container
    {
    padding-left: 5px !important;
    padding-right: 10px !important;
    }




/*  General  */

body
    {                                            /*  Header bar spacing  */
    padding-top: 50px !important;
    }

.dashhead
    {                                            /*  Title spacing (spacing for title popup)  */
    margin-bottom: 60px;
    }

.chapter-body
    {                                            /*  Text settings  */
    text-align: left;
    -moz-hyphens: none;
    }

.chapter-body sentence
    {                                            /*  "  */
    line-height: 1.25em;
    text-indent: 0em;
    letter-spacing: 0em;
    margin-bottom: 0.2em;                        /*  Line Break spacing - English  */
    }

.chapter-body .original
    {                                            /*  Line Break spacing - Chinese  */
    margin-bottom: 0.5em;
    }

.fontSizeBig .chapter-body, .dashhead-title, .popover-content, .popover-title, .popover
    {                                            /*  Text 1 size - default  */
    font-size: 27px !important;
    }

html, body, .chapter-badge, p, div ul li
    {                                            /*  Text 2 size - subtext + comments  */
    font-size: 17px !important;
    }

.chapter-body .original, .chapter-body .translated .popover-title, .popover-content
    {                                            /*  Text 3 size - Content + Chinese text size multiplier  */
    font-size: 130% !important;
    }

.chapter-body, .popover-content, .popover-title, .chapter-body, .fontSizeAutomatic .chapter-body
    {                                            /*  Text: standardized bold sans serif Chinese font  */
    font-family: Noto Sans SC !important;
    font-weight: 500;
    }

.popover.bottom > .arrow
    {                                            /*  Popup fix  */
    top: -10px;
    }

.popover.top > .arrow
    {                                            /*  "  */
    bottom: -10px;
    }

.popover
    {                                            /*  "  */
    padding: 5px !important;
    border-radius: 5px !important;
    max-width: 100%;
    }

.jumbotron, .alert, h3, .popover, .popover-content, .popover-title, .navbar, .navbar-default, .navbar-collapse
    {                                            /*  Border remove  */
    border-width: 0px !important;
    }

span.badge:nth-child(3)
    {                                            /*  Comments button # fix  */
    vertical-align: middle;
    top: 0px !important;
    }

.chapter-body
    {                                            /*  Content bottom spacing  */
    padding-bottom: 600px !important;
    }




/*  Comments  */
    
.modal-lg
    {                                            /*  Comments size  */
    width: 95%;
    }

.modal-dialog
    {                                            /*  Comments position  */
    margin-top: 75px;
    }




/*  Index  */

.frame ul li
    {                                            /*  Volume > button size  */
    width: 75px;
    justify-content: normal;
    font-size: 16px;
    }

.frame
    {                                            /*  Volume > frame size  */
    height: 150px;
    overflow: visible;
    }

.col-md-8
    {                                            /*  Main frame  */
    width: 70%;
    }

.col-md-4
    {                                            /*  Sidebar  */
    width: 30%;
    }




/*  Glossary  */

th.sorting:nth-child(2), th.sorting_disabled:nth-child(1)
    {                                            /*  Column 1 spacing: #  */
    width: 4% !important;
    }

th.sorting:nth-child(2), th.sorting_disabled:nth-child(2)
    {                                            /*  Column 2 spacing: Raw  */
    width: 10% !important;
    }

th.sorting:nth-child(3), th.sorting_disabled:nth-child(3)
    {                                            /*  Column 3 spacing: Meaning  */
    width: 46% !important;
    }

th.sorting:nth-child(4), th.sorting_disabled:nth-child(4)
    {                                            /*  Column 4 spacing: Novel  */
    width: 30% !important;
    }

th.sorting:nth-child(5), th.sorting_disabled:nth-child(5)
    {                                            /*  Column 5 spacing: Action  */
    width: 10% !important;
    }

.table > tbody > tr > td, .table > tbody > tr > th, .table > tfoot > tr > td, .table > tfoot > tr > th, .table > thead > tr > td,
.table > thead > tr > th
    {                                            /*  Cell spacing  */
    padding-top: 5px !important;
    padding-bottom: 5px !important;
    padding-right: 10px !important;
    }




/*  Content Colors  */

.popover, .popover-title, .backgroundColorDark .popover, .backgroundColorDark .popover-title
    {                                            /*  Popup BG 1                      def 1a1c22 dark grey     alt 555555 grey */
    background-color: #555555;
    }

.backgroundColorDark .popover.top > .arrow::after
    {                                            /*  Popup BG 2                      def 1a1c22 dark grey     alt 555555 grey */
    border-top-color: #555555;
    }

.backgroundColorDark .popover.bottom > .arrow::after
    {                                            /*  Popup BG 3                      def 1a1c22 dark grey     alt 555555 grey */
    border-bottom-color: #555555;
    }

body, .jumbotron, .alert-info, .pager li > a, .pager li > span, .panel, .panel-default > .panel-heading, .panel-footer
    {                                            /*  BG 1                            def cfd2da grey          alt 000000 black  */
    background-color: #000000 !important;
    }

.navbar-default, .dropdown-menu
    {                                            /*  BG 2                            def cfd2da grey          alt 000000 black  */
    background-color: #222222 !important;
    }

.backgroundColorDark .chapter-body t[aria-describedby], .backgroundColorDark .chapter-body w[aria-describedby]
    {                                            /*  BG text select                  def 1a1c22 dark grey     alt 4F4141 red-grey  */
    background-color: #4F4141;
    }

.chapter-body, .fontColorBlack .chapter-body, body, .navbar-default .navbar-nav > li > a, .navbar-default .navbar-text, .tt-input, 
.chapter-badge, .badge, .popover, .panel-heading, .panel-heading a, .backgroundColorDark .popover-content, .close, .close:focus, 
.close:hover, .dropdown-menu > li > a
    {                                            /*  Text (ENG, def)                 def 000000 black         alt FF0000 red  */
    color: #FF0000 !important;
    }

.chapter-body .original, .dashhead-subtitle
    {                                            /*  Text (CHI)                      def 000000 black         alt AA0000 dark red  */
    color: #AA0000 !important;
    }

sentence.translated > t, sentence.translated > dq > t
    {                                            /*  Text highlight (glossary ENG)   def 3959E4 dark blue     alt FFDD00 yellow  */
    color: #FFDD00 !important;
    }

sentence.original > t
    {                                            /*  Text highlight (glossary CHI)   def 3959E4 dark blue     alt FFBB33 dark yellow  */
    color: #FFBB33 !important;
    }

w.postfixed
    {                                            /*  Text highlight (system)         def 006C36 dark green    alt 00F086 aqua  */
    color: #00F086 !important;
    }

span.badge:nth-child(3), .dropdown-header
    {                                            /*  Text (comments button #)        def 000000 black         alt FFFFFF white  */
    color: #FFFFFF !important;
    }

.pagination > li > a, .pagination > li > span, .pagination > .disabled > a, .pagination > .disabled > span
    {                                            /*  Nav buttons                     def FFFFFF white         alt 333333 grey  */
    color: #FFFFFF;
    background-color: #333333;
    }




}
