@-moz-document domain("gogoanime.io"), domain("gogoanime.tv") {
/*banner nav*/
section.headnav{
    padding: 0;
    position: absolute;
    left: 0;
    background-color: #101010;
    min-height: 64px;
}
.form .row {		
    border-radius: 2px;		
    background-color: #2d2e2f;		
    border: none;		
}
nav.menu_top {		
    float: left;		
    width: 100%;		
    padding: 20px;		
    background-color: #101010;		
    margin:0;		
}
.banner .banner_left,
.menu_top_link,
.headnav_left,
::-webkit-scrollbar {
    display: none
}

#header_search_autocomplete_body{
    border: none;
    background-color: #101010;
    border-top: 1px solid red;
}
.list_search_ajax a{
    color: #b9b9b9;
}
.list_search_ajax a:hover {
    color: red;
}
nav.menu_top ul li a,
nav.menu_top ul li a {
    font-family: "roboto condensed", Helvetica, sans-serif;
    font-weight: 700;
    -webkit-font-smoothing: antialiased;
}
nav.menu_top ul li a:hover,
nav.menu_top ul li.active a {
    color: #e50914;
    border-bottom: 2px solid #e50914;
    font-family: "roboto condensed", Helvetica, sans-serif;
    font-weight: 700;
}
section.headnav .headnav_right{
    padding-right: 20px; 
}
.form .row input[type="button"] {
    background-image: none;
}

.form .row input[type="text"]{
    color: white;
}
@media only screen and (max-width: 1087px){
    section.headnav .headnav_right{
        width: 52vw;
        margin-left: auto;
        margin-right: auto;
        float:none;
        margin-top: 1.5vw;
        
    }
    #header_search_autocomplete{
        top: 3vw;
    }
    nav.menu_top ul{
        margin-top: 2.5vw
    }
    nav.menu_top ul li ul{
        padding-left: 0;
    }
    nav.menu_top ul li a{
        border-bottom: 2px solid #101010;
    }
    nav.menu_top ul li.user{
        display: none;
    }
}
}

@-moz-document regexp("(https:\\/\\/www\\d\\.gogoanime\\.io\\/)(((popular\\.html)|(new-season\\.html)|(anime-movies\\.html))(\\?page\\=\\d)?)?"), regexp("(https:\\/\\/www\\d\\.gogoanime\\.tv\\/)(((popular\\.html)|(new-season\\.html)|(anime-movies\\.html))(\\?page\\=\\d)?)?") {
/*home screen*/

div ul.items li, ul.items li:nth-child(5n),ul.items li:nth-child(4n+1) {
    margin-left: 1.25vw;
    width: 15vw;
}
div ul.items li:nth-child(5n+1){
    margin-left: 0;
}
.last_episodes{
	padding:0;
}
@media only screen and (max-width: 1087px) and (min-width: 1001px){
   ul.items li:nth-child(3n+1), ul.items li:nth-child(4n+1) {
        margin-left:1.25vw;
    }
}
@media only screen and (max-width: 1000px) and (min-width: 800px){
    ul.items li:nth-child(3n+1), ul.items li:nth-child(4n+1) {
        margin-left: 1.25vw;
    }
}
}

@-moz-document domain("gogoanime.io"), domain("gogoanime.tv") {
