    background-color: #e50d11
}
.anime_video_body div:nth-child(3), .anime_video_body_cate, div.menu_top_link, section.content_right, .anime_video_bodyp:nth-child(3), .anime_name.anime_video, ::-webkit-scrollbar {
    display: none
