css:
@-moz-document domain("trumptown.com") {
/*BEGIN 2019-03-14*/

body {
    background-color: #1b1b1b;
}
hr {
    border-top: 1px solid #1b1b1b;
    border-top-color: rgb(27, 27, 27);
}

.navbar-default {
    background: #303030;
    background-color: rgb(48, 48, 48);
}

.search-container .search-input {
    background: #000000 !important;
    background-color: rgb(0, 0, 0) !important;
}
.dropdown-menu {
    background-color: #303030;
}
.navbar-default .navbar-nav>li>a {
    color: #c0c0c0;
}
a.unread-update {
    color: #c0c0c0 !important;
}

.sidebar-profile-style {
    box-shadow: 0 1px 0 0 #e3e4e8, 0 0 0 1px #000000;
    background-color: #303030;
}
.sidebar-conatnier .profile-style .card.hovercard .info .title {
    color: #c0c0c0;
}
.wo_user_side_info li {
    border-right: 1px solid #1b1b1b;
    border-right-color: rgb(27, 27, 27);
}
.sidebar-profile-style {
    box-shadow: 0 1px 0 0 #1b1b1b, 0 0 0 1px #000000;
    background-color: #303030;
}


#filterby-post .filter_negative_clr, #filterby-post .filter_negative_clr svg {
    color: #ff6e6e;
}
#filterby-post .avtive {
    background: #303030;
    background-color: rgb(48, 48, 48);
}
.list-group-item {
    background-color: #303030;
    color: #555;
}
.list-group {
    box-shadow: 0 1px 0 0 #e3e4e8, 0 0 0 1px #f1f1f1;
    background-color: #303030;
}

.form-control {
    width: 100%;
    height: 34px;
    padding: 2px 12px;
    background-color: #303030;
    border: 1px solid #1b1b1b;
    border-top-color: #1b1b1b;
    border-right-color: #1b1b1b;
    border-bottom-color: #1b1b1b;
    border-left-color: #1b1b1b;
}
.form-control, .comment-textarea, .textarea, .ui-autocomplete-input {
    color: #c0c0c0;
}
.publisher-box .add-emoticons .dropdown-toggle {
    background: black;
    background-color: black;
}
.publisher-box textarea.postText {
    box-shadow: 0 1px 0 0 #000000, 0 0 0 1px #000000;
}
.publisher-box #camera-button-btn {
    color: #5b5b5b;
    background: black;
    background-color: black;
}
.post-textarea {
    border-top-color: #1b1b1b;
    border-right-color: #1b1b1b;
    border-bottom-color: #1b1b1b;
    border-left-color: #1b1b1b;
    box-shadow: 0 1px 0 0 #000000, 0 0 0 1px #000000;
}
.form-control .postText .ui-autocomplete-input {
    border: 1px solid #1b1b1b;
    border-top-color: #1b1b1b;
    border-right-color: #1b1b1b;
    border-bottom-color: #1b1b1b;
    border-left-color: #1b1b1b;    
    box-shadow: 0 1px 0 0 #000000, 0 0 0 1px #000000;
}

.list-group {
    box-shadow: 0 1px 0 0 #000000, 0 0 0 1px #000000;
    background-color: #303030;
}

.sidebar .sidebar-title-back {
    color: #444;
    background: #303030;
    background-color: rgb(48, 48, 48);
}
.sidebar .sidebar-title-back {
    color: #c0c0c0;
    background: #303030;
    background-color: rgb(48, 48, 48);
}

.order-by {
    color: #c0c0c0!important;
    border-bottom: 1px solid black;
    border-bottom-color: black;
}

.wo_sidebar_pages .page_middle .title a {
    color: #c0c0c0!important;
}

.wo_follow_btn {
    background: linear-gradient(to right,#115179 0,#115179 100%)!important;
    background-image: linear-gradient(to right, rgb(17, 81, 121) 0px, rgb(17, 81, 121) 100%) !important;
}
#wo_useract_btn {
    color: #c0c0c0;
}
.btn-main {
    color: #c0c0c0;
    background-color: #115179;
    border-color: #115179;
    border-top-color: #115179;
    border-right-color: #115179;
    border-bottom-color: #115179;
    border-left-color: #115179;
}

.list-inline, .wo_pro_users, slick-initialized, .slick-slider {
    background-color: #303030;
    box-shadow: 0 1px 0 0 #000000, 0 0 0 1px #000000;
}
.list-group-item, .text-muted, .sidebar-title-back {
    background-color: #303030;
}

.post-heading {
    background-color: #303030;
    box-shadow: 0 1px 0 0 #000000, 0 0 0 1px #000000;
}

.panel-white {
    box-shadow: 0 1px 0 0 #000000, 0 0 0 1px #f1f1f1;
}

.post-container {
    border-top-color: #1b1b1b;
    border-right-color: #1b1b1b;
    border-bottom-color: #1b1b1b;
    border-left-color: #1b1b1b;
}

.post .post-heading .meta .title a {
    color: #c0c0c0;
}

.post-fetched-url {
    border-radius: 3px;
    border: 1px solid #1b1b1b;
    border-top-color: #1b1b1b;
    border-right-color: #1b1b1b;
    border-bottom-color: #1b1b1b;
    border-left-color: #1b1b1b;
}

.post .post-description p {
    color: #c0c0c0;
}

#wo_post_stat_button .stat-item {
    color: #616770;
    background: #303030;
    background-color: rgb(48, 48, 48);
}
#wo_post_stat_button .stat-item {
    color: #89929f;
    background: #303030;
    background-color: rgb(48, 48, 48);
}
.like-emo > .how_reacted {
    background: #303030;
    background-color: rgb(48, 48, 48);
}

.post .post-description .post-actions .stat-item {
    background: #303030;
    background-color: rgb(48, 48, 48);
}

#wo_comment_combo .textarea {
    background-color: #000;
    border: 1px solid #1b1b1b;
    border-top-color: rgb(27, 27, 27);
    border-right-color: rgb(27, 27, 27);
    border-bottom-color: rgb(27, 27, 27);
    border-left-color: rgb(27, 27, 27);
}

#wo_comment_combo.comment-toggle {
    background-color: #303030;
    border-color: #1b1b1b;
    border-top-color: rgb(27, 27, 27);
    border-right-color: rgb(27, 27, 27);
    border-bottom-color: rgb(27, 27, 27);
    border-left-color: rgb(27, 27, 27);
}
#wo_comment_combo .comment_combo_footer {
    background-color: #303030;
}

.post-fetched-url .fetched-url-text h4 {
    color: #c0c0c0;
}
a {
    color: #b0b0b0;
}

.post .post-footer {
    border-top: 1px solid black;
    border-top-color: black;
    background: #303030;
    background-color: rgb(48, 48, 48);
    box-shadow: 0 1px 0 0 #000000, 0 0 0 1px #000000;
}

#wo_comment_combo.comment-toggle .textarea {
    border-color: #303030;
    border-top-color: rgb(48, 48, 48);
    border-right-color: rgb(48, 48, 48);
    border-bottom-color: rgb(48, 48, 48);
    border-left-color: rgb(48, 48, 48);
}

#wo_sidebar_users {
    outline: #000000 solid 1px;
}

.wo_sidebar_groups {
    border-bottom: 1px solid #1b1b1b;
}

#wo_comment_combo {
    border-top: 1px solid black;
    border-top-color: black;
}

.post-description {
    background-color: #303030;
    box-shadow: 0 1px 0 0 #000000, 0 0 0 1px #000000;
}

/*Profile menu*/
.navbar-default .dropdown-menu.ani-acc-menu >li>a {
    transition: none;
    color: #c0c0c0;
    padding: 6px 16px;
    font-size: 13px;
}

/*END 2019-03-14*/


/*BEGIN 2019-03-17*/
.comment-text, .reply-text {
    color: #c0c0c0;
}
/*Profile link color on comments*/
.h1, .h2, .h3, .h4, .h5, .h6, h1, h2, h3, h4, h5, h6 {
    color: #8090f0;
}
/*END 2019-03-17*/


/*BEGIN 2019-04-06*/
.user-request-list .user-info a {
    color: #c0c0c0;
}
.request-list li {
    border-bottom: 1px solid #000000;
    border-bottom-color: rgb(0, 0, 0);
}

.element.style {
    background: #303030;
    background-color: rgb(48, 48, 48);
}

li.pro-me-here.pull-right {
    background-color: #303030;
}
.featured-users .sidebar-title-back, .featured-users .pro-me-here a {
    color: #ccc !important;
}

/*My profile*/
/*top bar*/
a {
    background-color: #303030;
}
div.user-bottom-nav.col-md-10v {
    background-color: black;
}
/*Profile Completion*/
ul.profile-completion-bar {
    background: #303030;
    background-color: rgb(48, 48, 48);
}
ul.profile-completion-bar li.percentage-bar h4 {
    background: #303030;
    background-color: rgb(48, 48, 48);
}
ul {
    background-color: #303030;
}
/*END My Profile*/

element.style {
    background: #303030;
    background-color: rgb(48, 48, 48);
}

/*Albums*/
.my_pages_header {
    background: #303030;
    background-color: rgb(48, 48, 48);
    border: 1px solid #303030;
    border-top-color: rgb(48, 48, 48);
    border-right-color: rgb(48, 48, 48);
    border-bottom-color: rgb(48, 48, 48);
    border-left-color: rgb(48, 48, 48);
}

/*Blogs*/
.main-blog-sidebar .popular-articles li .article-title {
    color: #ccc;
}
.main-blog-sidebar .popular-articles li .article-info {
    color: #ccc;
}
.blog-h-list {
    background: #303030;
    background-color: rgb(48, 48, 48);
    border: 1px solid #000;
    border-top-color: rgb(0, 0, 0);
    border-right-color: rgb(0, 0, 0);
    border-bottom-color: rgb(0, 0, 0);
    border-left-color: rgb(0, 0, 0);
}
.blog-h-list h3 {
    color: #ccc;
}
.hidden-xs {
    color: #c0c0c0;
}
.postFooter .u-floatRight a {
    color: #c0c0c0;
}
.blog-h-list .middot {
    color: #c0c0c0;
}
.main {
    color: #369dd9 !important;
}
.blog-h-desc p.desc {
    color: #c0c0c0;
}

/*Blog*/
.read-blog-container .read-blog .read-blog-head {
    background: #303030;
    background-color: rgb(48, 48, 48);
}
.list-inline, .wo_pro_users, slick-initialized, .slick-slider {
    background-color: #303030;
    box-shadow: 0 1px 0 0 #303030, 0 0 0 1px #303030;
}
.read-blog-container .read-blog h1 {
    color: #c0c0c0;
}
.read-blog, .read-blog .blog-author-info {
    background: #303030;
    background-color: rgb(48, 48, 48);
}
.read-blog-container .read-blog .read-blog-desc {
    color: #999;
}
.read-blog-container .read-blog .read-content {
    color: #c0c0c0;
}
.form-control {
    width: 100%;
    height: 34px;
    padding: 2px 12px;
    background-color: #000000;
    border: 1px solid #404040;
    border-top-color: #404040;
    border-right-color: #404040;
    border-bottom-color: #404040;
    border-left-color: #404040;
}
.read-blog {
    border: 1px solid #0000;
    border-top-color: rgba(0, 0, 0, 255);
}
.read-tags {
    border-top: 1px solid #1b1b1b;
    border-top-color: rgb(27, 27, 27);
}
.read-blog-container .read-tags .postTag {
    border: 1px solid #0000;
    background: #1b1b1b;
    background-color: rgb(27, 27, 27);
}
.read-blog .blog-author-info {
    border: 1px solid #1b1b1b;
    border-top-color: rgb(27, 27, 27);
    border-right-color: rgb(27, 27, 27);
    border-bottom-color: rgb(27, 27, 27);
    border-left-color: rgb(27, 27, 27);
}
.blog-sidebar, row {
    background-color: #303030;
}
.read-blog-container .related-post .blog-sidebar h4.title {
    background-color: #303030;
}
.read-blog-container .related-post {
    border: 1px solid #303030;
    border-right-color: rgb(48, 48, 48);
    border-bottom-color: rgb(48, 48, 48);
    border-left-color: rgb(48, 48, 48);
}
.read-blog-container .related-post .recommended-articles .article-title h4 {
    color: #c0c0c0;
}
.blog-com-wrapper, .movies-com-wrapper {
    width: 100%;
    overflow: hidden;
    padding: 10px;
    background-color: #303030;
}
.blog-com-wrapper {
    border: 1px solid #303030;
    border-right-color: rgb(48, 48, 48);
    border-bottom-color: rgb(48, 48, 48);
    border-left-color: rgb(48, 48, 48);
}
/*END 2019-04-06*/


/*BEGIN 2019-04-16*/

/*Pro Members tab*/
element.style {
    background: #303030;
    background-color: rgb(48, 48, 48);
}
}
