css:
@document url-prefix(http://studia.elka.pw.edu.pl/), url-prefix(https://studia.elka.pw.edu.pl/)
{
:root {
    --bgColor: #fefefe;
}

html{
    background-color: var(--bgColor);
}

.tlo{
    background-color: var(--bgColor);
}

a{
    color:#07a;
}
a:visited{
    color:#ac5a82;
}

body{
    font-family:Open Sans,Arial;
    color:#454545;
    font-size:16px;
    margin:2em auto;
    max-width:900px;
    padding:1em;
    line-height:1.4;
    text-align:justify;
    background-color:  var(--bgColor);
}

.modules_title{
    background-color: var(--bgColor);
	color: #000000;
	font-weight: bold;
	display: inline-block;
	padding: 2px 6px 2px 6px;
	border: 1px solid #c0c0c0;
}

.nagl, .zaw0, .zaw{
    background-color: var(--bgColor);
}


.modules_lbls li {
    background-color: var(--bgColor);
	border: 1px solid #c0c0c0;
}

table{
    border-collapse: collapse;
}

th, tr {
    border-bottom: 1px solid black;
    border-top: 1px solid black;
    border-collapse: collapse
}

th[colspan="6"], th[width="22"]{
    display: none;
}

td:not([class]){
    visibility: hidden;
}

h2{
    visibility: hidden;
}

.modules_subs{
    visibility: visible;
}
}
