css:
@-moz-document domain("kogama.com") {
/*-------------------------------------------------------*/
/*------------------------BODY---------------------------*/
/*-------------------------------------------------------*/
/*███BODY███*/

body
{
    font-family: "Arial Black" !important;
}

/*-------------------------------------------------------*/
/*-------------------------ID----------------------------*/
/*-------------------------------------------------------*/

#content-container
{
    background-image: url(https://www.wallpaperup.com/uploads/wallpapers/2012/10/14/19257/f3f1dcc53bb7c1c84bd2b2b29caca52a-700.jpg) ;
    background-attachment: fixed
}

/*_____________________________________*/
/*███PROFILE FEEDS███*/

#profile-news-feed ul.news-feed-thumbs > li.item 
{
    background-color: #13091599;
    color: white;
}

/*-------------------------------------------------------*/
/*----------------------KLASY----------------------------*/
/*-------------------------------------------------------*/

.user
{
    color: white !important;
    font-weight: bold;
}

/*_____________________________________*/
/*███INPUT FILL███*/
.input-fill
{
    background-color: #00000047 !important;
    color: white;
}

/*_____________________________________*/
/*███TEXT███*/

.text
{
    color: #fff !important;
}

/*_____________________________________*/
/*███BUTTONS███*/

.pure-button.pure-button-primary.button-fill,
.pure-button.pure-pure-button-small.submit-comment.pure-button-primary
{
    background-color: #4125578a
}

/*_____________________________________*/
/*███GAMES AND AVATARS IMAGES███*/

.display-image
{
    opacity: 0.55 !important;
}

/*_____________________________________*/
/*███CHAT EXTENDED SIDE███*/

._1Yhgq
{
    background-image: url(https://www.pixelstalk.net/wp-content/uploads/2016/04/Stars-wallpaper-HD-desktop.jpg) !important;
}

/*_____________________________________*/
/*███REPORT BUTTON███*/

.report-button-toggle.pure-button.pure-button-secondary.pure-button-xsmall
{
    background-color: #ff00003b !important;
    color: #ffffffa3 !important;
}

.report-button-types
{
    color: #fff !important;
    background-color: #c16b6be0 !important;
}

/*_____________________________________*/
/*███USER PROFILE HEADER███*/

.username
{
    text-align: center !important;
    margin-bottom: 2.5% !important;
    border-bottom: 10px !important;
}
.progression
{
    margin-left: 32.5% !important;
}
.level
{
    background-color: #fff0 !important;
}
#mobile-page #profile-page .section-top .username h2 
{
    line-height: 6.5rem;
    font-size: 5rem;
    
}

/*_____________________________________*/
}
