/* ==UserStyle==
@name           Diaspora Blurry SFW
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    Expands #nsfw posts and blurs the images until you mouse-over them.
@author         Gible Fog
==/UserStyle== */

@-moz-document regexp("https://(pluspora.com|iliketoast.net|diaspora-fr.org|socializer.cc|diaspora.podzimek.org|social.mrzyx.de|diasporabr.com.br|pod.jpope.org|diapod.net|diaspora.lots.li|pod.fulll.name|diasp.org)/.*") {
.nsfw-shield{display:none}
.stream-element>.media.shield-active .nsfw-hidden{display:block}
.stream-element>.media.shield-active .nsfw-hidden img {filter:blur(20px)}
.stream-element>.media.shield-active .nsfw-hidden img:hover{filter:blur(0px)}
}
