css:
@-moz-document url-prefix("https://www.silvergames.com/") {
.content {
    background-color: #000; /*#fff*/
}
body {
    background-color: #111; /*#f0f0f0*/
}
.sbox .h, .tag a {
    background-color: #222; /*#e0e0e0*/
}
.w-300, .homebox .h {
    background-color: #333; /*#d0d0d0*/
}
#game-box {
    background-color: #444; /*#c0c0c0*/
}
.highlighted {
    border: 1px solid #07293a !important; /*#9ec5d9*/
    background-color: #00202f !important; /*#daf2fe*/
}
.highlighter:hover {
    box-shadow: 0 2px #07293a!important;
}
.footer {
    background-color: #111;
    color: #a0a0a0; /*#606060*/
    border-top: 1px solid #333;
}
.flex_thumb_list2 li:hover {
    background-color: #222;
    box-shadow: 0 2px #555; /*#b0b0b0*/
}
.flex_thumb_list2 li {
    border: 1px solid #222;
    box-shadow: 0 1px #383838; /*#d8d8d8*/
}
.sbox {
    border: .063em solid #222;
}
.r2 {
    color: #000;
    background-color: #5c5c5c; /*#a3a3a3*/
}
.r1, .r2 a {
    color: #000;
    border-bottom: .063em solid #000;
}
.pager a {
    border: .063em solid #606060; /*#a0a0a0*/
    color: #a0a0a0;
    background: #000;
}
.game_bottom_box {
    background-color: #000;
    border: 1px solid #181818; /*#e8e8e8*/
    box-shadow: 0 1px #444;
}
div[style="padding:20px 10px;margin-top:20px;border-top:1px solid #d0d0d0; "] {
    border-top: 1px solid #333 !important; 
}
#webmasters h2 {
    color: #c0c0c0; /*#404040*/
}
}
