css:
@-moz-document domain("swish.swi-prolog.org") {
.CodeMirror {
    background: #222;
}
.navbar-default .navbar-collapse, .navbar-default .navbar-form {
    background: #222;
}
.nav-tabs {
    background: #222;
}
.nav {
    background: #222;
}
.CodeMirror-gutter, .CodeMirror-gutters, .CodeMirror-linenumber, .CodeMirror-scroll, .CodeMirror-sizer {
    background: #222;
}
.nav-tabs>li.active>a, .nav-tabs>li.active>a:focus, .nav-tabs>li.active>a:hover {
    color: #ffffff;
    background: #222;
}

.splitter_panel .bottom_panel, .splitter_panel .hsplitter, .splitter_panel .left_panel, .splitter_panel .right_panel, .splitter_panel .top_panel, .splitter_panel .vsplitter {;
    background: #222;
}

.cm-s-prolog span.cm-goal_built_in {
    color: #fff;
}

.cm-s-prolog span.cm-flag_name {
    color: #00e5ff;
}
body {
    color: #fff;
}

.CodeMirror pre {
    color: white;
}
.cm-s-prolog span.cm-functor {
    color: #fff;
    font-style: italic;
}
.cm-s-prolog span.cm-atom {
    color: #c45400;
}
.cm-s-prolog span.cm-var {
    color: #d00000;
}
.cm-s-prolog span.cm-singleton {
    color: #f00;
}

.cm-s-prolog span.cm-comment {
    color: #0b0;
}
div.prolog-runners {
    background-image: none;
}
.cm-s-prolog span.cm-goal_autoload {
    color: #0099f9;
}
div.prolog-query {
    background-color: inherit; 
}
CodeMirror-cursor {
    background: #fff;
    color: white;
    border: 1px solid white;
}
.CodeMirror div.CodeMirror-cursor {    
    background: #fff;
    color: white;
    border: 1px solid white;
}
inner {
    background: #222;
    color: black;
}
div.prolog-runner {
    border-radius: 5px;
    background: #222;
    color: white;
}
.panel .panel-default {
    background: #222;
}
.panel-default>.panel-heading {
    color: white;
    background: #222;
    border-color: #222;
}
div.runner-results {
    padding: 2px 0 2px 0;
    background: #222;
    border-radius: 5px;
}
div.answer.odd {
    background-color: inherit;
}
div.answer.even {
    background-color: inherit;
}
.glyphicon {
    color: white;
}
button {
    color: white;
    background: #222;
    border-radius: 5px;
    border: 1px solid #4a4a4a;
}
pre.msg-warning {
    color: red;
}
pre.prolog-message {
    background: #000;
}
.CodeMirror .source-msg>span.glyphicon {
    color: white;
}
}
