/* Color Standards: */
:root {
	--black: #000;
	--slate: #13141a;
	--slate2: #20212d;
	--slate3: #455a64;
	--white: #eee;
	--gray: #d7d7d7;
	--gray2: #7b808c;
	--gold: #dfc846;
	--goldbright: gold;
}
/* Max-width standard: */
:root {
	--pwidth: 990px !important;
}
.m-newsfeed {max-width: var(--pwidth) !important;}
body > m-app > m-body > m-channel > div.mdl-grid.channel-grid.ng-star-inserted {max-width: var(--pwidth) !important;}

/* The outer and upper main bg colors */
.mdl-color--grey-100 {background-color: var(--slate) !important;}
.mdl-color--white {background-color: var(--black) !important;}
/* 'light theme' disabler */
body.m-theme__light m-app m-body {background-color: var(--slate)!important;}
body.m-theme__light .m-v2-topbar__Top {background-color: var(--black) !important; border: none;}
body.m-theme__light .m-border {border: none;}
body.m-theme__light textarea {background-color: transparent !important;}
body.m-theme__light .mdl-color--blue-grey-50 {background-color: var(--slate) !important;}
body.m-theme__light .m-comment-wrapper .m-comments-composer form minds-textarea, body.m-theme__light .m-comment-wrapper .minds-editable-container textarea, body.m-theme__light m-comments__tree .m-comments-composer form minds-textarea, body.m-theme__light m-comments__tree .minds-editable-container textarea, body.m-theme__light minds-comments .m-comments-composer form minds-textarea, body.m-theme__light minds-comments .minds-editable-container textarea {background-color: var(--gray) !important;}
body.m-theme__light .minds-avatar {background-color: transparent !important;}
body.m-theme__light .m-v2-topbar__Avatar .minds-avatar {background-color: var(--white) !important;}
body.m-theme__light .m-comment__ribbonBtn {color: var(--white) !important; background-color: transparent !important;}
body.m-theme__light m-app .mdl-card__title-text {color: var(--white) !important;}
body.m-theme__light .m-title-block, body.m-theme__light .m-title-block-fixed {background-color: var(--black) !important;}
body.m-theme__light .m-read-more--button {background: linear-gradient(to bottom,rgba(255,255,255,0) 0, var(--black) 50%);}
.m-messenger--conversation .m-messenger--conversation-composer textarea {color: var(--white); background-color: var(--slate2) !important;}
body.m-theme__light {color: var(--white); background-color: var(--slate);}
body.m-theme__light .m-messenger--userlist .m-messenger--userlist-search {background-color: transparent;}
/* Topbar */
m-topbar .m-topbar--logo {background-color: var(--black) !important;}
m-search--bar .mdl-textfield input {background: var(--white);}
m-search--bar .mdl-textfield input {box-shadow: inset 0 0 50px var(--black);}
.m-topbar--navigation--item i {color: var(--gold) !important;}
.m-topbar--navigation--item.m-topbar--navigation--item-active {border-top: 4px solid var(--gold)}
body > m-app > m-topbar {border-bottom: 0px solid var(--white);}
body > m-app > m-topbar > div.m-topbar--row > div.m-dropdown.m-topbar--account.ng-star-inserted > a > img {box-shadow: 0 0 25px var(--white);}
.m-v2-topbar__Top {background: var(--black); border: none;}
/* Tweaks notifications */
.mdl-color--blue-grey-50 {background-color: var(--slate) !important;}
m-notifications--flyout .notifications-grid .mdl-cell--12-col minds-notification .body p {color: var(--white);}
m-topbar .m-topbar--icons i.material-icons {color: var(--gray);}
m-notifications--topbar-toggle .m-notifications--topbar-toggle--icon--active::after {background: var(--gold);}
m-notifications--topbar-toggle > m-notifications--flyout > div > div.m-notifications--flyout--scrollable-container > minds-notifications > div.mdl-grid.notifications-grid > div > minds-notification {box-shadow: inset 0 0 5px var(--white);}
m-notifications--toaster > div > minds-notification {border: 1px solid var(--white); box-shadow: 0 0 25px var(--white);}
.mdl-tabs__tab-bar {background-color: var(--slate2);}
minds-notifications .mdl-tabs__tab .m-icon, minds-notifications .mdl-tabs__tab .material-icons {color: var(--gray) !important;}
m-notifications--flyout {box-shadow: 0 0 15px var(--slate2);}
/* Boost buttons */
.m-btn--action {color: var(--gold) !important;}
.m-btn--action {border: 1px solid var(--gold);}
.minds-boost-button {background-color: var(--goldbright)!important;}
.minds-boost-button {visibility: hidden !important;}
/* Groups sidebar bg */
.m-sidebarMarkers__container {background: var(--slate) !important;}
.m-groupSidebarMarkers__list li img {box-shadow: 0 0 5px var(--white);}
.m-groupSidebarMarkers__list li {border-bottom: none;}
.m-groupSidebarMarkers__list .has-marker::after {background: var(--gold);}
/* Profile dropdown */
.m-dropdown .m-dropdown__list {background-color: var(--slate2);}
.m-dropdownList__item a {color: var(--white);}

/* Posts and comments */
.mdl-card__supporting-text {color: var(--white) !important;}
.mdl-card {background: var(--black) !important;}
.m-owner-block a {color: var(--gray2)}
.minds-editable-container textarea {color: var(--black) !important;}
minds-textarea>.m-editor {color: var(--black) !important;}
.post>div form .mdl-textfield__input {color: var(--white) !important;}
.m-comment-wrapper .m-comment-attachment .m-rich-embed, .m-comment-wrapper .m-comments-composer .m-rich-embed, .m-comment-wrapper .m-editable-attachment-container .m-rich-embed, minds-comments .m-comment-attachment .m-rich-embed, minds-comments .m-comments-composer .m-rich-embed, minds-comments .m-editable-attachment-container .m-rich-embed {background-color: var(--slate2);}
.m-title-block, .m-title-block-fixed {background-color: var(--black);}
.m-read-more--button {background: linear-gradient(to bottom,rgba(255,255,255,0) 0, var(--black) 50%);}
/* Uses soft borders */
.m-border {border: none;}
.m-border {box-shadow: 0 0 25px var(--black), 0 0 1px var(--white) !important;}
.m-page .m-page--sidebar--navigation--item {border: none;}
m-modal .m-modal-container .mdl-card {box-shadow: 0 0 15px var(--white), inset 0 0 10px var(--white) !important;}
/* Post action bar */
.m-poster .m-poster__ActionBar .m-posterActionBar__IconAndLabel>span, .m-poster .m-poster__ActionBar .m-posterActionBar__Label {display: none !important;}
.m-poster .m-poster__ActionBar>* {color: var(--gray2);}
m-newsfeed--subscribed > minds-newsfeed-poster > div > div > form > div > button {color: var(--goldbright) !important;}
m-channel--feed > minds-newsfeed-poster > div > div > form > div > button {color: var(--goldbright) !important;}
minds-groups-profile-feed > minds-newsfeed-poster > div > div > form > div > button {color: var(--goldbright) !important;}
m-post-menu > div {display: none !important;}
body > m-app > m-body > m-newsfeed > div.mdl-grid.m-newsfeed.m-page > div.mdl-cell.mdl-cell--8-col.m-newsfeed--feed > m-newsfeed--subscribed > div > minds-activity > div.mdl-card__supporting-text.m-activity--pending {color: var(--black) !important; background: var(--white) !important;}

/* Channel banner */
minds-banner {box-shadow: inset 0 0 100px var(--slate);}
minds-banner > div > div.minds-banner-img.m-banner--img-cover {background-blend-mode: overlay;}
m-channel--carousel>i {color: var(--gold);}
.m-channel--name {color: var(--white);}
/* Avatars */
.minds-avatar {background: none;}
m-channel .m-channel-sidebar .m-channel--bio minds-avatar .minds-avatar, m-channel .m-channel-sidebar .mdl-card minds-avatar .minds-avatar, m-channel .minds-channel-sidebar .m-channel--bio minds-avatar .minds-avatar, m-channel .minds-channel-sidebar .mdl-card minds-avatar .minds-avatar {box-shadow: 0 0 50px var(--white), inset 0 0 10px var(--white);}
.m-owner-block .avatar img {box-shadow: 0 0 10px var(--white);}
m-newsfeed--subscribed > div > minds-activity > minds-comments > div.minds-comments-container > minds-card-comment > div > div.minds-avatar {box-shadow: none;}
.m-media-content--meta .m-media-content--owner .minds-avatar img {box-shadow: none;}
.m-comment .minds-avatar img {box-shadow: none;}
m-notifications--topbar-toggle > m-notifications--flyout > div > div.m-notifications--flyout--scrollable-container > minds-notifications > div.mdl-grid.notifications-grid > div > minds-notification > div.avatar > a > img {box-shadow: 0 0 10px var(--white)}
m-notifications--toaster > div > minds-notification > div.body > div > a {color: var(--white) !important;}
minds-notification > div.avatar > a > img {box-shadow: 0 0 10px var(--white);}
/* Rewards widget */
.mdl-color--blue {background-color: var(--gold) !important;}
.mdl-color--blue {display: none !important;}
m-wire-channel-table > div {background-color: var(--slate2);}
/* Blogs */
m-app .mdl-card__title-text {color: var(--white);}
.mdl-color-text--blue-grey-700 {color: var(--white) !important;}
.m-blog--tile .m-blog--tile-title label {color: var(--white);}
.m-blog--tile {background: var(--black);}
m-blog-view, minds-blog-view {background-color: var(--black);}
.m-blog .m-blog-container h1.m-blog--title {color: var(--white);}
.minds-blog-body p {color: var(--white);}
.minds-blog-body {background-color: var(--slate); box-shadow: inset 0 0 25px var(--black);}
m-blog-view a {color: aqua;}
m-blog-view > div > minds-comments > div.m-comment.m-comment--poster.minds-block.ng-star-inserted > div.minds-body > div > div > a {color: var(--gold);}
m-blog-view > div > minds-comments > div.minds-comments-container > minds-card-comment > div > div.minds-body > div.m-comment__bubble > div > a {color: var(--slate3);}
m-blog-view > div > minds-comments > div.minds-comments-container > minds-card-comment > div > div.minds-body > minds-comments > div.minds-comments-container > minds-card-comment > div > div.minds-body > div.m-comment__bubble > div > a {color: var(--slate3);}
m-blog-view > div > minds-comments > div.minds-comments-container > minds-card-comment > div > div.minds-body > minds-comments > div.m-comment.m-comment--poster.minds-block.ng-star-inserted > div.minds-body > div > div > a {color: var(--gold);}
minds-blog-edit > form > div.mdl-grid > div.mdl-cell.mdl-cell--12-col > minds-textarea > div {color: var(--white);}
minds-blog-edit {background-color: var(--slate2);}
minds-blog-edit > form > div.mdl-cell.mdl-cell--12-col.minds-blog-descriptions > m-inline-editor > div > p {color: var(--white);}
/* Media pages */
.m-media-content--wrapper .m-media-content--container {background: var(--black); color: var(--white);}
m-media--theatre {background-color: var(--black) !important;}
.m-commentBubble__message {color: var(--black);}
/* .m-media-recommended.ng-star-inserted {display: none !important;} */

/* Messenger */
.m-messenger--dockpane-tab {background: var(--black) !important; color: var(--white) !important;}
.m-messenger--dockpane-tab-title > i {color: var(--gold);}
.m-messenger--dockpane-container {background: var(--slate) !important; border: 1px solid var(--white);}
.m-messenger--dockpane-container > div.m-messenger--userlist-search {background-color: var(--slate2);}
.m-messenger--dockpane-container > div.m-messenger--userlist-search > input[type="text"] {background-color: var(--slate2); color: var(--white);}
.m-messenger--userlist-conversations > div > img {border: 1px solid var(--gold);}
.m-messenger--userlist-conversations > div > span.m-conversation-label {color: var(--gray);}
.m-messenger--userlist-conversations > div > span.m-conversation-icons > span.m-conversation-new.mdl-color--green {background-color: var(--gold) !important; display: inline-block;}
.m-messenger--userlist-conversations > div > span.m-conversation-icons > span.m-conversation-online.mdl-color--blue {background-color: navy !important; display: inline-block;}
.m-messenger--dockpane .m-messenger--dockpane-tab-ribbon
.m-messenger--conversation-composer.ng-star-inserted > textarea {background-color: var(--slate2); color: var(--white);}
.m-messenger--conversation-composer.ng-star-inserted {background-color: var(--slate2);}
.m-messenger--conversation-composer.ng-star-inserted > i:nth-child(2) {visibility: hidden;}
.m-messenger--conversation .m-messenger--conversation-messages .m-messenger--conversation-message-bubble {background-color: var(--white) !important; color: var(--black) !important;}
.m-messenger--conversation .m-messenger--conversation-messages .m-messenger--conversation-messages-message.odd .m-messenger--conversation-message-bubble {background-color: #98a4b7!important}
 
/* Scrollbars */
::-webkit-scrollbar {
    width: 9px;
    background: var(--slate);
}
::-webkit-scrollbar-thumb {
    background: var(--gray);
    background-image: -webkit-linear-gradient(top, var(--slate), #cab867 40%, #f0c400 90%);
    -webkit-border-radius: 0.4ex; /* rounding */
    -webkit-box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.75);
}
::-webkit-scrollbar-corner {
    background: var(--slate);
}

/* DISCRETIONARY: moves sidebars to right; NOTE: transform breaks fixed-position modal elements*/
body > m-app > m-body > m-channel > div.mdl-grid.channel-grid.ng-star-inserted {
    transform: scaleX(-1);}
m-channel .m-channel-sidebar, m-channel .minds-channel-sidebar {
    transform: scaleX(-1);
	margin-top: 8px;}
body > m-app > m-body > m-channel > div.mdl-grid.channel-grid.ng-star-inserted > section.mdl-cell.mdl-cell--8-col.m-channel-feed.ng-star-inserted {
    transform: scaleX(-1);}

body > m-app > m-body > m-newsfeed > div.mdl-grid.m-newsfeed.m-page{
	transform: scaleX(-1);}
body > m-app > m-body > m-newsfeed > div.mdl-grid.m-newsfeed.m-page > div.mdl-cell.mdl-cell--8-col.m-newsfeed--feed {
	transform: scaleX(-1);}
.m-newsfeed__sidebar.m-newsfeed--sidebar.mdl-cell--4-col.mdl-cell {
	transform: scaleX(-1);}
.m-page .m-page--sidebar--navigation--item {background: var(--gray2) !important; color: var(--white) !important;}

m-channel .m-channel-sidebar, m-channel .minds-channel-sidebar {
	transform: scaleX(-1);}
body > m-app > m-body > m-channel > div.mdl-grid.channel-grid {
	transform: scaleX(-1);}
body > m-app > m-body > m-channel > div.mdl-grid.channel-grid > section.mdl-cell.mdl-cell--8-col.m-channel-feed {
	transform: scaleX(-1);}

body > m-app > m-body > m-newsfeed > div.mdl-grid.m-newsfeed.m-page > div.mdl-cell.mdl-cell--4-col.m-newsfeed--boost-sidebar.m-newsfeed__sidebar {transform: scaleX(-1);}

m-modal .m-modal-container { /*attempted fix to modal display */
	position: absolute !important;
	transform: translate(-50%,-10%);
}
/* DISCRETIONARY: removes channel explicit overlays, replaces logo, and hides dislikes */
m-channel--explicit-overlay {display: none !important;}
body > m-app > m-v2-topbar > div.m-v2-topbar__Top > div > div.m-v2-topbar__Container--left > nav > a.m-v2-topbarNav__Item.m-v2-topbarNavItem__Logo > img {content: url("https://i.imgur.com/jKrsxux.png");}
/* minds-button-thumbs-down {visibility: hidden !important;} */
m-channel--badges > ul > li > m-tooltip > div > i {content: url("https://upload.wikimedia.org/wikipedia/commons/thumb/4/49/Star_of_David.svg/1200px-Star_of_David.svg.png"); width: 20px;}
div.m-comment__bubble > div > a > m-channel--badges > ul > li > m-tooltip > div > i {width: 15px;}

body > m-app > m-v2-topbar > div.m-v2-topbar__Top > div > a > minds-avatar > div {content: url("https://upload.wikimedia.org/wikipedia/commons/f/f7/Nazi_swastika_clean.svg");}
body > m-app > m-v2-topbar > div.m-v2-topbar__Top > div > a > minds-avatar > div {background-image: none !important; background-color: var(--white);}
.m-v2-topbar__Avatar.m-v2-topbar__Avatar--active .minds-avatar {box-shadow: 0 0 0 2px #3ba0ff;}
.m-v2-topbar__Avatar .minds-avatar {border: none !important; box-shadow: inset 0 0 0 5px var(--white);}
}

@-moz-document url("https://www.minds.com/newsfeed/subscribed"), url("https://www.minds.com/newsfeed/subscriptions") {
/* Removes the boost at the top of the page */
m-newsfeed--boost-rotator.ng-star-inserted {display: none !important;}
.ng-star-inserted.m-newsfeed--dynamic-siderbar {display: none !important;}
.m-newsfeed--boost-sidebar.m-newsfeed__sidebar.ng-star-inserted {display: none !important;}
body > m-app > m-body > m-newsfeed > div.mdl-grid.m-newsfeed.m-page > div.mdl-cell.mdl-cell--8-col.m-newsfeed--feed > m-newsfeed--subscribed > div > m-newsfeed--boost-rotator {display: none !important;}
body > m-app > m-body > m-newsfeed > div.mdl-grid.m-newsfeed.m-page > div.mdl-cell.mdl-cell--4-col.m-newsfeed--sidebar.m-newsfeed__sidebar > div.m-newsfeed--dynamic-siderbar {display: none !important;}
body > m-app > m-body > m-newsfeed > div.mdl-grid.m-newsfeed.m-page > div.mdl-cell.mdl-cell--4-col.m-newsfeed--boost-sidebar.m-newsfeed__sidebar > m-ads-boost {display: none !important;}
body > m-app > m-body > m-newsfeed > div.mdl-grid.m-newsfeed.m-page > div.mdl-cell.mdl-cell--4-col.m-newsfeed--boost-sidebar.m-newsfeed__sidebar {display: none !important;}

