css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("5ch.net") {
body {
    background-color: #FFF;
    background-image:none;
}
a,
a:link{
    color: #ccc;
}
.AA {
    font-family: inherit!important;
    color: #ccc;
}
/* list */
div{
    background-color: #fff !important;
    padding: 0 !important;
    border-color: transparent !important;
    border-radius: 0 !important;
}
.HEADER_AREA,
.THREAD_MENU,
.THREAD_MENU p{
    background-color: transparent !important;
    padding: 0 !important;
    border-color: transparent !important;
    border-radius: 0 !important;
}
.THREAD_MENU div{
    border-top-color: transparent !important;
    border-bottom-color: transparent !important;
}
font,
strong{
    color: #999;
    font-size: 14px !important;
    font-weight: 500 !important;
}
h1 a{
    display: block;
    width:92px;
    height:30px;
    /* background:transparent url("https://www.google.com/images/branding/googlelogo/2x/googlelogo_color_92x30dp.png") 50% 50% no-repeat; 
    background-size:contain; */
}
h1 img {
    display: none;
}
h1+p{
    display:none;
    margin: 0 !important;
    text-align: left !important;
}
h1.title{margin-top: 60px;}
h2 span{
    background-color: #999 !important;
    font-size: 14px !important;
}
h3{
    text-indent: 0 !important;
    padding: 0 !important;
}
h3 span,
.RES_NUMBER span{
    font-size: 14px !important;
    color: #666 !important;
    background-color: transparent !important;
    padding: 0 !important;
}
h3 + form input,
textarea{
    color: #666 !important;
    background-color: transparent !important;
    border-color: transparent !important;
}
h3 + form p{
    margin-left: 0 !important;
}
.board_header{
    height: 5em !important;
}
.board_header p{
    text-align: left !important;
}
dl.thread dt,
dl.thread dt small{
    font-size: 10px !important;
    color: #ddd !important;
    overflow: hidden;
    margin: 0 !important;
    transition-duration: .5s;
    transition-timing-function: ease-in-out;
}
dl.thread dt{
    border-top: 1px dotted #ccc;
}
dl.thread dt:hover{
    color: #999;
}
dl.thread dd{
    margin: 0 0 1.0em 0 !important;
}

/* thread */
.search-header {
    background: #fff;
    border-top: none !important;
    padding: 10px 0;
}
.search-logo a{
    display: block;
    width:92px;
    height:30px;
    background:transparent url("https://www.google.com/images/branding/googlelogo/2x/googlelogo_color_92x30dp.png") 50% 50% no-repeat;
    background-size:contain;
}
.search-logo img {
    display: none;
}
.container_body{
    margin: 0 1em !important;
}
.mascot{
    background-image:none;
    background-position-x: calc(100% + 250px);
}
.topnav,
.bottomnav{
    float: left;
}
.menubottomnav {
    min-width: 100px;
}
.topmenu,
.bottommenu{
    border-bottom: none;
    float: left;
}
.menujust {
    margin: .5em 0;
}
.menuitem {
    border: 1px solid #FFF;
}
.menuitem:hover {
    border: 1px dotted blue;
}
.itest,
.highlight,
.ula{
    color: #ccc !important;
}
.newposts {
    margin-bottom: 10px;
    margin-top: 10px;
}
.newpb {
    border: none;
}
.socialmedia {
    padding-bottom: 13px;
    padding-top: 13px;
}
.social {
    margin-right: 5px;
}
.iconsize {
    width: 16px;
    height: 16px;
}
.formbox {
    padding-bottom: 10px;
    max-width: none;
}

.formelem {
    margin-bottom: 10px;
    border: none;
}
.formheader{
    font-size: 14px;
    font-weight: 500;
    border-bottom: none;
}
.btn{
    padding: 4px 10px;
    font-size: 14px;
    background-color: #ccc
}
.oekaki_load1{
    color: #ccc
}
.footer {
    padding-top: 10px;
    font-size: 10px;
}
.footer br {display:none;}

.meta {
    font-size: 10px;
    color: #ddd;
    /* max-height: 0; */
    overflow: hidden;
    transition-duration: .5s;
    border-top: 1px dotted #ccc !important;
    /* transition-delay: .5s; */
    transition-timing-function: ease-in-out;
}
.post:hover .meta{
    /* max-height: 100px; */
    color: #999;
}
.name{
    font-size:inherit;
    color: inherit;
}
.name a{
    color: inherit;
}
.message{
    padding: 5px 0 8px;
}
.post {
    margin-bottom: 10px !important;
    padding: 0;
    width: 100%;
    border-style: none none solid none;
    border-color: #eee;
    background-color: transparent;
}
.post_hover {
    padding: 0 5px 5px !important;
    border-radius: 5px !important;
}
/* ad */
.ad--right,
.ad--bottom,
.ADVERTISE_AREA,
.ADVERTISE_AREA + div,
#banner,
#horizontalbanners199,
#horizontalbanners399,
#horizontalbanners599,
#horizontalbanners799,
iframe{
    display:none;
}

@media only screen and (max-width: 2000px) {
    .search-right {
        /* float: right; */
        display: inline-block;
        margin-right: 0px;
        vertical-align: middle;
        font-weight: 500;
    }
    .search-premium>span>a>span {
        display: none;
    }
    .search-premium>span>a>img {
        margin-right: -10px!important;
    }
    .search-login>a>span {
        display: none;
    }
    .search-login>a>img {
        margin-right: -10px!important;
    }
    .search-setting>span>span {
        display: none;
    }
    .search-settings>img {
        height:21px!important;
    }
    #search-desktop > input[type="text"] {
        width: 480px;
    }
}
}
