css:
@-moz-document domain(moodle.selu.edu) {
body {
    background-color: #1b1b1d !important;
    color: white !important;
}
#page-header{
    margin-top: 40px !important;
}
#region-main h1,
h2,
h3,
h4,
h5,
h6 {
    color: white !important;
}
p, a, span, strong, h1, h2, h3, h4, h5, div, li, td, th  {
    font-size: 14px;

}

.forumpost, .forumheaderlist{
    background: #3a3a3a;
}
body .forumpost .subject{
    margin: 5px;
    font-size: 20px;
    color: white;
}


div.main, table, #page #page-content #region-main > div[role="main"],
.pagelayout-redirect #page-content #region-main > div[role="main"],
#page #page-content #region-main,
#page-login-index .loginpanel,
#block-region-side-pre .block,
.loginbox .potentialidps,
.block,
h1  {
    background-color: #1b1b1d !important;
    color: white !important;
}

.row{
    margin-left: -10px;
    overflow: overlay;
}

#page #page-content #region-main > div[role="main"],
.pagelayout-redirect #page-content #region-main > div[role="main"] a {
    font-size: 15px;
}

.row-fluid .desktop-first-column, tr, .highlight {
