}

.dropdown-menu, 
.popover-title {
    background: #3a3a3c !important;
}

.initialbar .letter{
    background: #1b1b1d !important;
}

img.userpicture{
    position: relative;
    z-index: 10;
}

body .fa, em, .notyetanswered, .trafficlight{
    color: #f45531 !important;
    background: #3a3a3c !important;
}

span.mceText{
    color: white !important;
}

.popover, .card{
