css:
@-moz-document domain("poedb.tw") {
/* layout */
.panel-default {
    border: none !important;
}
.table-bordered {
	border: none !important; /* sorry */
}
.col-md-4>table {
    table-layout: fixed;
}
.col-md-4>table tr>th:first-child,
.col-md-4>table tr>td:first-child {
    width: 64px;
}
.col-md-4>table td {
    overflow-wrap: break-word;
}

a.badge,
.tags span {
    float: left;
	padding: 1px 5px;
    margin: 3px;
    border-radius: 0.15em;
}
.tags:after{
    content: "";
	display: table;
	clear:both;
}

.panel-body>ul.double>li:not(.col-xs-6) {
	margin: 25px 0 15px;
    padding-top: 15px;
}


.label,
span.badge {
	font-size: 0.75em;
	font-weight: normal;
	display: inline-block;
	vertical-align: middle;
	padding: .3em .6em .3em;
	margin-bottom: 5px;
}
.label{
	border-radius: 0.15em;
}
span.badge {
	border-radius: 1em;
}
.mod-title {
	margin: 0 !important;
	padding: 4px 5px 4px 5px;
	border-radius: 0.15em !important;
	background-clip: content-box !important;
}
caption {
	margin-top: 30px;
}
h4 {
	margin: 30px 0 20px;
}
caption>h4 {
	margin: 0 0 10px;
}
div.dataTables_filter {
	margin: 50px 0 -15px;
}


/* search */

.query input[type="text"],
input[type="search"] {
	padding: 2px 4px;
	border-radius: 0.15em !important;
    font-weight: normal;
}
.query input[type="text"]{
	display: inline-block;
	vertical-align: middle;
}

/* button */

.query input[type="reset"]{
	display: inline-block;
	vertical-align: middle;
	padding: 3px 12px;
	margin-bottom: 0;
	font-size: 14px;
	font-weight: 400;
	line-height: 1.42857143;
	text-align: center;
	white-space: nowrap;
	border-radius: 4px;
}

div.mod-title:hover,
div.mod-title[style*="background-color: rgb(217, 237, 247);"]:hover,
div.mod-title[style*="background-color: rgb(188, 232, 241);"]:hover,
div.mod-title[style*="background-color: rgb(0, 169, 203);"]:hover,
div.mod-title[style*="background-color: rgb(233, 235, 238);"]:hover {
	border: none;
	background-clip: content-box;
}

.modal-footer {
    border: none;
}

/* colors */

.navbar-inverse .navbar-nav > li > a {
    color: #f4f4f4;
}

.navbar-default {
    background-image: linear-gradient(rgba(20,20,20,1) 0%, rgba(33,33,33,1) 100%) !important;
    background-color: rgb(28,28,28) !important;
	border: none !important;
	box-shadow: inset 0 -1px 0 rgba(255,255,255,.15);
}
.navbar-default .navbar-nav > li > a { color: #ccc; }
.page .navbar-default li > a { color: #aba899; text-shadow: none; }
.navbar-default .navbar-nav > li > a:hover { color: #d2c9a0; } 
.navbar-default .navbar-nav > li > a:focus { 
    color: #555;
    text-shadow: none;
}

.panel-default > .panel-heading {
    background-image: none !important;
    background-color: transparent !important;
    color: #f0ad4e;
    border-style: none none none none;
    border-width: 1px;
    border-color: #aaa;
}
.panel-title {color: #f2e9c0;}
.panel-body>ul.double>li:not(.col-xs-6) {
    border-style: none none none none;
    border-width: 1px;
    border-color: #aaa;
}

a:hover {
    color: #ffeea3;
}
hr {
	border-color: transparent !important;
}
h4, .navbar-default .navbar-brand {
	color: #d2c9a0 !important;
    background-color: transparent !important;
}
.navbar-default .navbar-nav > .active > a, 
.navbar-default .navbar-nav > .open > a {
	background-image: none !important;
	box-shadow: none !important;
}
.navbar-default .navbar-nav > .open > a, 
.navbar-default .navbar-nav > .open > a:focus, 
.navbar-default .navbar-nav > .open > a:hover {
	background-color: rgba(200,200,200,0.1);
    color: #ffeea3;
}
.dropdown-menu {
	background-color: #333;
}
.dropdown-menu > li > a {
	color: #c0bba2;
}
.dropdown-menu > li > a:focus, .dropdown-menu > li > a:hover {
	background-color: #696969 !important;
	background-image: none !important;
	color: #e6e2d1 !important;
}

/* red gems */
a.gem_red, .gem_red, .mod_corrupted {
	color: #ff4335;
}
/* green gem */
a.gem_green, .gem_green {
	color: #44e370;
}

/* search input */

input, textarea {
	background-color: #333 !important;
	color: #ddd !important;
}
.dataTables_wrapper .dataTables_filter {
    color: #9f9c8d !important;
}
.query input[type="text"],
input[type="search"] {
	border: 1px solid #4a4a4a;
}
.query input[type="text"]:hover,
input[type="search"]:hover {
	border: 1px solid #666;
}
.query input[type="text"]:focus,
input[type="search"]:focus {
	border: 1px solid #888;
	background-color: #333 !important;
}

/* button */

.query input[type="reset"]{box-shadow: inset 0 1px 0 rgba(255,255,255,.15),0 1px 1px rgba(0,0,0,.075);}
.query input[type="reset"],
.btn-default {
	background-image: none;
    background-color: #333;
    border: none;
    color: #ccc !important;
    text-shadow: none;
}
.query input[type="reset"]:hover,
.btn-default:hover {
    background-color: #444;
    color: #eee !important;
}
.query input[type="reset"]:focus,
.btn-default:focus {
    background-color: #585858 !important;
    color: #fff !important;
}

a.badge,
.tags span {
	background-color: #333;
	color: #9f9c8d;
    border: none;
    box-shadow: inset 0 1px 0 rgba(255,255,255,.15),0 1px 1px rgba(0,0,0,.075);
}
a.badge:hover,
.tags span:hover {
	background-color: #444;
	color: #b9b5a3;
}

/* table rows and cells */

/* reset TD color */
.table > tbody > tr > td {
	background-color: transparent !important;
}

/* set odd/even TR colors */
.page,
.page-content,
.page-content .col-lg-6,
/*.table > tbody > tr > td,*/
.modal-content,
table.dataTable tbody tr.even,
table.dataTable tbody tr.even,
table.dataTable tbody tr.even > .sorting_1 {
	background-color: #0f0f0f !important;
	color: #9f9c8d !important;
}
.table-striped > tbody > tr:nth-of-type(2n+1),
table.dataTable tbody tr,
table.dataTable tbody tr.odd,
table.dataTable tbody tr.odd,
table.dataTable tbody tr.odd > .sorting_1,
.modal-content tbody>tr:nth-child(2n+1)>td {
	background-color: #1e1e1e !important;
}

table.dataTable tbody th, 
table.dataTable tbody td,
.table-bordered > tbody > tr > th,
.table-bordered > tbody > tr > td {
    border-top: 1px solid #323232 !important;
    border-bottom: none !important;
    border-left: none !important;
    border-right: none !important;
}


.label {
	color: #bbb;
}
.label-primary {
    background-color: #113f66;
}
span.badge {
    background-color: #b78100;
    color: #fff;
}

.modal-content {
	border: 1px solid #4a4a4a;
    box-shadow: 0 0 10px 0 #333;
}
.modal-body table,
.tablesorter table, .tablesorter td, .tablesorter th {
	border: none !important;
}
.modal-body table th {
	color: #e9e1bb !important;
}


/* don't touch bg color (need for script), do with inset shadow */
.mod-title {
	box-shadow: inset 0 0 0 100px #0f0f0f;
	color: #9f9c8d !important;
    border-color: #0f0f0f !important;
}
.mod-value { color: #63c2c2 !important; }
.ilvl-test { color: #555 !important; }


/* default: rgb(217, 237, 247) = #d9edf7 */
.mod-title[style*="background-color: rgb(217, 237, 247);"] { 
	box-shadow: inset 0 0 0 100px #0f0f0f;
    border-color: #0f0f0f !important;
}

/* higher chance: rgb(188, 232, 241) = #bce8f1 */
.mod-title[style*="background-color: rgb(188, 232, 241);"] { 
	box-shadow: inset 0 0 0 100px #333;
    color: #c0bba2 !important;
    border-color: #333 !important;
}
.mod-title[style*="background-color: rgb(188, 232, 241);"] .ilvl-test { color: #888 !important; }

/* HIGHEST chance: rgb(0, 169, 203) = #00a9cb */
.mod-title[style*="background-color: rgb(0, 169, 203);"] {
	box-shadow: inset 0 0 0 100px #666;
    color: #e9e1bb  !important;
    border-color: #666 !important;
}
.mod-title[style*="background-color: rgb(0, 169, 203);"] .mod-value { color: #d0f3f0 !important; }
.mod-title[style*="background-color: rgb(0, 169, 203);"] .ilvl-test { color: #aaa !important; }


/* lower chance: rgb(233, 235, 238) = #e9ebee */
.mod-title[style*="background-color: rgb(233, 235, 238)"] {
	box-shadow: inset 0 0 0 100px #000;/**/
	color: #5a5a5a !important;
    border-color: #000 !important;
}
.mod-title[style*="background-color: rgb(233, 235, 238);"] .mod-value {
	color: #376c6c !important;
}


/* hover for all chances */
div.mod-title:hover,/* default */
div.mod-title[style*="background-color: rgb(217, 237, 247);"]:hover,/* default */
div.mod-title[style*="background-color: rgb(188, 232, 241);"]:hover,/* higher */
div.mod-title[style*="background-color: rgb(233, 235, 238);"]:hover /* lower */{
    border: 1px solid #a98 !important;
}
div.mod-title[style*="background-color: rgb(0, 169, 203);"]:hover /* HIGHEST */{
    border: 1px solid #eed !important;
}
}
