@-moz-document domain("poedb.tw") {
/* layout */
.panel-default {
    border: none !important;
}
.table-bordered {
	border: none !important; /* sorry */
}
.col-md-4>table {
    table-layout: fixed;
}
.col-md-4>table tr>th:first-child,
.col-md-4>table tr>td:first-child {
    width: 64px;
}
.col-md-4>table td {
	/*line-break: normal;*/
    overflow-wrap: break-word;
}

a.badge,
.tags span {
    float: left;
    /*vertical-align: middle;/**/
	padding: 1px 5px;
    margin: 3px;
    /*line-height: 2;/**/
    border-radius: 0.15em;
}
.tags:after{
    content: "";
	display: table;
	clear:both;
}

.panel-body>ul.double>li:not(.col-xs-6) {
	margin: 25px 0 15px;
    padding-top: 15px;
}


.label,
span.badge {
	font-size: 0.75em;
	font-weight: normal;
	display: inline-block;
	vertical-align: middle;
	padding: .3em .6em .3em;
	margin-bottom: 5px;
}
.label{
	border-radius: 0.15em;
}
span.badge {
	border-radius: 1em;
}
.mod-title {
	margin: 0 !important;
	padding: 4px 5px 4px 5px;/**/
	border-radius: 0.15em !important;
	/*border-style: none !important;/**/
	background-clip: content-box !important;
}
caption {
	margin-top: 30px;
}
h4 {
	margin: 30px 0 20px;
}
caption>h4 {
	margin: 0 0 10px;
}
div.dataTables_filter {
	margin: 50px 0 -15px;
}


/* search */

.query input[type="text"],
input[type="search"] {
	padding: 2px 4px;
	border-radius: 0.15em !important;
    font-weight: normal;
}
.query input[type="text"]{
	display: inline-block;
	vertical-align: middle;
}

/* button */

.query input[type="reset"]{
	display: inline-block;
	vertical-align: middle;
	padding: 3px 12px;
	margin-bottom: 0;
	font-size: 14px;
	font-weight: 400;
	line-height: 1.42857143;
	text-align: center;
	white-space: nowrap;
	border-radius: 4px;
}

div.mod-title:hover,
div.mod-title[style*="background-color: rgb(217, 237, 247);"]:hover,
div.mod-title[style*="background-color: rgb(188, 232, 241);"]:hover,
div.mod-title[style*="background-color: rgb(0, 169, 203);"]:hover,
div.mod-title[style*="background-color: rgb(233, 235, 238);"]:hover {
	border: none;
	background-clip: content-box;
}

.modal-footer {
    border: none;
}

