css:
@-moz-document url-prefix("http://game.wsmud.com") {
body {
    font-family: "Hiragino Sans GB";
    background-color: #0006;
}


.channel {
    position: fixed;
    top: 0;
    left: 0;
    width: 25%;
    min-height: 99%;
    text-shadow: 3px 3px 3px #000000f0;
    background-color: #0006;
}


.content-message {
    position: fixed;
    top: 0;
    right: 0;
    width: 20%;
    bottom: 10px;
    text-shadow: 3px 3px 3px #000000f0;
    background-color: #0006;
}
.map {
    position: fixed;
    top: 20%;
    z-index: 15;
    border-style: ridge;
    background: #000000f5;
    overflow: auto;
}

.right-bar {
    position: fixed;
    right: 22%;
}


.WG_log {
    flex: 1;
    overflow-y: auto;
    border: 1px solid #404000;
    max-height: 25em;
    width: calc(100%);
    text-shadow: 3px 3px 3px #000000f0;
}


.channel pre {
    font-family: "黑体";
    font-size: 90%;
}

.content-message pre {
    font-family: "楷体";
    font-size: 110%;
}

.obj-desc {
    font-family: "楷体";
    font-size: 110%;
}

.fb-right pre {
    font-family: "楷体";
    font-size: 110%;
}

.fb-left {
    font-family: "Hiragino Sans GB";
    font-size: 110%;
}

.layui-layer {
    border-style: ridge;
    background: #5e5e5ef5;
    overflow: auto;
}
}
