css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("herokugarden.com"), domain("heroku.com") {


textarea, #sidebar_middle_middle, #main_middle_middle {
  /* Background color for editor window */
  background: #28211C !important;
}

#editor {
  /* Font for editor window */
  font-family: Monaco, "Courier New", monospace !important;
  /* Color for non-syntax words */
  color: #BAAE9E !important;
}


/* Selected text */
::-moz-selection {
  background: #ffb7b7; /* Firefox */
}

/* Cursor */
#editor #cursor {
  background-color:#A7A7A7 !important;
}

/* Ruby Syntax */
.string {
  color: #5ff800 !important;
}
.keyword {
  color: #F56400 !important;
}
.classname {
  color: #feca00 !important;
  text-decoration: underline !important;
}
.symbol {
  color: #319896 !important;
}
.blockparams {
  color: #df0ddb !important;
}
.instancevar {
  color: #ffffff !important;
}
.comment {
  color: #9832cb !important;
}
.comment span {
  /* this blocks symbol highlighting within a comment */
  color: #9832cb !important;
}

/* ERB Syntax */
.erb_brace {
  color: #077 !important;
}
.erb_ruby {
  color: #6ee !important;
}
.html_tag_brace {
  color: #660 !important;
}
.html_tag{
  color: #bb0 !important;
}

/* CSS Syntax */
.css_property_name {
  color: #97965e !important;
}
.css_property_value {
  color: #aaa !important;
}

}
