form+tbody td[align='right']+td {display:block;width:100%}

form[onsubmit="search_submit(); return false;"] {background: #fff; padding: 20px; border-radius: 16px;}
form[onsubmit="search_submit(); return false;"] {position:relative}
form[onsubmit="search_submit(); return false;"] td {font-weight: bold; color: #808682; font-size: 14px}
form[onsubmit="search_submit(); return false;"] tr:last-child {position: absolute; right: 20px; bottom: 20px}
input[type="radio"] {visibility: hidden; width:20px}
input[type="radio"]::before {visibility: visible; display:inline-block; width: 6px; height:6px; border-radius:50%; border: 5px solid #f4f5f8; content: ''; margin-right: 20px; background:#f4f5f8}
label:hover input[type="radio"]::before, input[type="radio"]:hover::before {border-color:#ccc;background:#fff}
input[type="radio"]:checked::before {border-color:#828893 !important; background:#fff}

input[type="checkbox"] {visibility: hidden; width:20px}
input[type="checkbox"]::before {visibility: visible; display:inline-block; width: 16px; height:16px; content: ''; margin-right: 20px; background:#f4f5f8}
label:hover input[type="checkbox"]::before, input[type="checkbox"]:hover::before {background:#ccc}
input[type="checkbox"]:checked::before {background:#828893 !important; color:#fff; content: '\f00c'; font-family:"Font Awesome 5 Free"; font-weight: bold; text-align: center; line-height: 16px; font-size: 10px}

#content>p[align="center"] {font-size: 0;min-height: 52px}
#content>p[align="center"]>b, #content>p[align="center"] a, #index>b>a {color:#808682; font-size: 16px; padding: 12px 20px; display:inline-block; margin: 5px}
#content>p[align="center"]>b {color:#a8aab4}
#content>p[align="center"] a, #index>b>a {background:#fff; border-radius: 16px; display:inline-block}
#content>p[align="center"] a:hover, #index>b>a:hover {background:rgba(0,0,0,0.05);}
#content>p[align="center"] a:hover b, #index>b>a:hover b {color:#000}
#content>p[align="center"] br {display:none}
script + p[align="center"]>*:first-child,
fieldset + p[align="center"]>*:first-child,
#index + p[align="center"]>*:nth-last-child(2),
table + p[align="center"]>*:nth-last-child(2) {float:left; margin-left:0 !important; font-size: 0 !important}
script + p[align="center"]>*:nth-child(2),
fieldset + p[align="center"]>*:nth-child(2),
#index + p[align="center"]>*:last-child,
table + p[align="center"]>*:last-child {float:right; margin-right: 0 !important; font-size: 0 !important}
script + p[align="center"]>*:first-child::before,
fieldset + p[align="center"]>*:first-child::before,
fieldset + p[align="center"]>*:nth-child(2)::before,
script + p[align="center"]>*:nth-child(2)::before,
#index + p[align="center"]>*:nth-last-child(2)::before,
#index + p[align="center"]>*:last-child::before ,
table + p[align="center"]>*:nth-last-child(2)::before,
table + p[align="center"]>*:last-child::before {content:'\f100'; font-weight: bold; font-family:"Font Awesome 5 Free"; font-size:16px !important;}
fieldset + p[align="center"]>*:nth-child(2)::before,
#index + p[align="center"]>*:last-child::before,
table + p[align="center"]>*:last-child::before,
script + p[align="center"]>*:nth-child(2)::before {content:'\f101'}
#index>b {float:left; word-spacing: 30px;}
#index>b>a {margin: 5px -10px}
#index {text-align: right; font-weight: bold; padding-top: 20px; color: #808682; font-size: 16px;}
#index>*:first-child {margin-top:-20px}
#index>* {text-align: left}

fieldset[style="width: 300px;"] {display: inline-block; width: calc(33.33% - 16px) !important; margin-right: 20px; background:#fff;vertical-align: top; border-radius: 16px; box-sizing: border-box; padding: 20px 40px; position: relative}
fieldset[style="width: 300px;"] legend {background:#fff; width: 100%; padding-top:20px;border-radius: 16px 16px 0 0; position:relative; left: -40px;}
#content fieldset[style="width: 300px;"]:nth-of-type(3) {margin-right: 0}
fieldset[style="width: 300px;"] td {font-weight: bold; color: #808682}
fieldset[style="width: 300px;"] td, fieldset[style="width: 300px;"] a {font-size: 14px}

fieldset + h1 { font-weight: bold; text-align:center; color:#d6662a; border: 3px solid #d6662a; padding: 20px; border-radius: 16px}

form[action^="/comment"] + tbody {display:block; background:#fff; width: calc(100% - 200px); position: absolute; border-radius: 16px; padding: 20px; }
form[action^="/comment"] + tbody td {font-size: 14px; font-weight: bold; color:#808682;}
form[action^="/comment"] + tbody input[type="button"] {border:0; padding: 6px 10px; border-radius: 8px; margin-bottom: 10px}
form[action^="/comment"] + tbody input[type="button"]:hover {background: rgba(0,0,0,0.1) !important}
#vkb_callVirt {display:none}

h2 + h3, h3 + h3 {background:#fff; border-radius: 16px; padding: 20px; font-weight: bold; color:#808682}
h3 + h3 {margin-top: -60px}
h2 small {font-size:0 !important;}
h2 small a::before {content:'\f059'; font-size: 20px; font-family:"Font Awesome 5 Free"; font-weight: normal; margin-left: 5px}

#ui-datepicker-div {z-index: 2; background: #e39d40; border-radius: 16px; color:#fff; border: 0; padding: 20px}
.ui-widget-header {border: 0; background: transparent; padding: 0}
.ui-datepicker .ui-datepicker-header {padding: 0}
.ui-widget-header select {border: 1px solid #e39d40; border-bottom:2px solid #fff; color:#fff}
.ui-widget-header select option { color:#000}
.ui-datepicker .ui-datepicker-prev span, .ui-datepicker .ui-datepicker-next span { background:transparent !important; margin-top: 0; top: 7px}
.ui-datepicker .ui-datepicker-prev, .ui-datepicker .ui-datepicker-next {top: 8px}
.ui-datepicker-prev::before, .ui-datepicker-next::before {content:'\f100'; font-weight: bold; font-family:"Font Awesome 5 Free"; width: 23px; height:23px; line-height:23px; text-align:center; display:inline-block}
.ui-datepicker-next::before {content:'\f101'}
.ui-state-default, .ui-widget-content .ui-state-default {background: rgba(255,255,255,0.8); color:#808682; padding: 4px 8px; border: 0; border-radius: 8px}
.ui-state-hover, .ui-widget-content .ui-state-hover, .ui-state-focus, .ui-widget-content .ui-state-focus {background: #fff; border: 0; border-radius: 8px; color:#000}
.ui-datepicker {width: auto}
.ui-state-active, .ui-widget-content .ui-state-active {background:#d6662a; color:#fff}
.ui-datepicker th {color:#eee}

.u-block {margin: 0; border: 0; background: transparent;}
.u-block h2 {background: transparent; padding: 20px 40px; border-radius: 16px; font-weight: bold; margin-bottom: 10px; font-size: 20px; }
div.u_space {background: #fff; border: 0;  border-radius: 16px; font-weight: bold; color: #808682; font-size: 14px; padding: 20px 40px; margin-bottom: 10px;}
div.u_space h3 {font-weight: bold}
#dropdeck th, #dropdeck td {font-weight: bold; color: #808682; font-size: 14px}
#dropdeck table {width: 100%}
span[style="position: absolute; top: 5px; right: 5px;"] {top: 0 !important; right: 0 !important; background: #fff; border-radius: 16px; padding: 14px 40px;}
.bbBar {background:#f4f5f8; border-radius: 8px}
.bbBar .btn {padding: 8px 16px; height: auto; background: transparent; font-weight: bold; color: #4f4f4f }
.bbBar .btn:hover {background: #ccc}
#dropdeck input[type=text], #dropdeck textarea, #dropdeck select {color:#3f3f3f !important}
.opts a {background: transparent; color:#1f1f1f; border: 0}
input[name="file"] {border: 0; font-weight: bold}
#patterns a {margin-right: 10px}
#rules h2:hover, #preview h2:hover, h2.hover {background:transparent; color:#999}
#rules small[style="display: inline;"]:first-child::before, #rules small:first-child::before, #rules small[style="display: none;"]:first-child::before {content:'\f068'; font-family:"Font Awesome 5 Free"; font-size: 14px; margin-right: 10px;}
#rules small[style="display: none;"]:first-child {display:inline-block !important}
#rules small[style="display: none;"]:first-child::before {content:'\f067';}

.loading {background-image: -webkit-gradient(linear, 0 0, 100% 100%, color-stop(.25, rgba(255, 255, 255, .2)), color-stop(.25, transparent), color-stop(.5, transparent), color-stop(.5, rgba(255, 255, 255, .2)), color-stop(.75, rgba(255, 255, 255, .2)), color-stop(.75, transparent), to(transparent) ) !important;background-color: #d6662a !important;border-radius: 4px; animation: move 2s linear infinite !important; background-size: 50px 50px !important; background-repeat: repeat !important}

@keyframes move {
  0% {
    background-position: 0 0;
  }
  100% {
    background-position: 50px 50px;
  }
}

