css:
@-moz-document domain("dissenter.com") {
.hydra .comment-page-list .comment-container{
background: #1a1a1a;
border-width: 0 0 1px 0;
border-color: #292929;
}

.hydra .comment-page-list .comment-container{
margin-bottom:0
}
.hydra .hydra-container{
padding: 0
}

.comment-composer-container form.comment-composer{
margin-left: 10px;
margin-right: 10px;
}


.comment-content{
padding-top:10px;
}
.hydra div.comment-container .comment-card div.comment{
padding: 10px
}

.hydra div.comment-container .comment-card div.comment .comment-header{
margin-bottom: 0!important;
}


.hydra div.comment-container .comment-card .comment-replies{
padding-left: 20px;
}

.hydra div.comment-container .comment-card .comment-replies .comment-composer-embedded form.comment-composer div:first-child{
display: none;
}
.hydra div.comment-container .comment-card .comment-replies .comment-composer-embedded form.comment-composer .help-block.text-muted{
display:none;
}
.hydra div.comment-container .comment-card .comment-replies .comment-composer-embedded form.comment-composer .form-control{
position:relative;
}
.hydra div.comment-container .comment-card .comment-replies .comment-composer-embedded form.comment-composer div:last-child{
justify-content: flex-end!important;
}

.hydra div.comment-container .comment-card .comment-replies .comment-composer-embedded form.comment-composer div:last-child button{
width: 130px;
background: #00b868!important;
margin-right: 0!important;
}
.hydra div.comment-container div.comment-card .comment-replies .comment-composer-embedded form.comment-composer div:last-child button:hover{
background: #007241 !important
}

.hydra div.comment-container .comment-card .comment-reply{
background: #1a1a1a;
border-width: 1px;
border-right: 1px solid;
border-color: #292929;
margin-right: 15px;
border-radius: 5px;
}

.comment-control-bar .nav-pills .nav-item a{
display: flex;
height: 20px;
background-position: start;
border: none;
align-items: center;
text-align: start;
padding-left: 30px;
}

.comment-control-bar .nav-pills{
padding: 0 10px 0 10px;
flex-wrap: nowrap;
}


.comment-control-bar .nav-pills .nav-item {
flex-grow: 0;
margin-right: 10px;
background-repeat: no-repeat;
}

.comment-control-bar .nav-pills .nav-item  .nav-link.active{
color: #00b868 !important
}

.comment-control-bar .nav-pills .nav-item:first-child a{
    		background-image: url(data:image/svg+xml;base64,);
}

.comment-control-bar .nav-pills .nav-item:first-child a.active{
	background-image: url(data:image/svg+xml;base64,);
}


.comment-control-bar .nav-pills .nav-item:first-child:hover a:not(.active){
	background-image: url(data:image/svg+xml;base64,);
}


.comment-control-bar .nav-pills .nav-item:nth-child(2) a{
    	background-image: url(data:image/svg+xml;base64,);
}
.comment-control-bar .nav-pills .nav-item:nth-child(2) a.active{
	background-image: url(data:image/svg+xml;base64,);
}

.comment-control-bar .nav-pills .nav-item:nth-child(2):hover a:not(.active){
	background-image: url(data:image/svg+xml;base64,);
}

.comment-control-bar .nav-pills .nav-item:nth-child(3) a{
    	background-image: url(data:image/svg+xml;base64,);
}

.comment-control-bar .nav-pills .nav-item:nth-child(3) a.active{
    	background-image: url(data:image/svg+xml;base64,);
}

.comment-control-bar .nav-pills .nav-item:nth-child(3):hover a:not(.active){
	background-image: url(data:image/svg+xml;base64,);
}


.comment-control-bar .nav-pills .nav-item:nth-child(4) a{
    					background-image: url(data:image/svg+xml;base64,);
}
.comment-control-bar .nav-pills .nav-item:nth-child(4) a.active{
							background-image: url(data:image/svg+xml;base64,);
}

.comment-control-bar .nav-pills .nav-item:nth-child(4):hover a:not(.active){
						background-image: url(data:image/svg+xml;base64,);
}


.comment-replies-content .comment-control-bar{
display: none;
}

#begin-composer-container form div:first-child{
display: none;
}

.hydra .comment-composer-container form .form-group .help-block.text-muted{
display: none;
}

.hydra .comment-composer-container form.comment-composer div:last-child{
justify-content: flex-end!important;
}

.hydra .comment-composer-container form.comment-composer div:last-child button{
width: 130px;
background: #00b868!important;
margin-right: 0!important;
}
.hydra .comment-composer-container form.comment-composer div:last-child button:hover{
background: #007241 !important
}
.comment-replies{
position:relative;
}
.comment-replies:after{
content:"";
width: 5px;
height: 100%;
background-color: #00b868;
position: absolute;
top: 0;
    border-radius: 5px
}
.comment-replies .comment-card{
position:relative;
}


.comment-replies-content .comment-reply.comment-container{
margin-left: 25px!important;
}

.comment-replies .comment-card:after{
content:"";
width: 15px;
height: 15px;
border-radius: 100%;
background-color: #00b868;
position: absolute;
top: 50%;
left: -31px;
}

.hydra-container:first-child > div:nth-child(1){
padding-left: 10px
}

.comment-footer a i{
font-size: 0;
}

.comment-footer .col-auto a span{

margin-left:25px
}
.comment-footer .col-auto a:hover{
text-decoration:none;
}

a.text-success,a.text-success:hover{
color: #00b868 !important;
}


a.text-danger,a.text-success:hover{
color: #E51D18 !important;
}


.comment-footer .col-auto:first-child a i{
height:20px;
width:20px;
position:absolute;

	background-image: url(data:image/svg+xml;base64,);
}
.comment-footer .col-auto:first-child a:hover i{
height:20px;
width:20px;
position:absolute;

		background-image: url(data:image/svg+xml;base64,);
}

.comment-footer .col-auto:first-child a.text-success i{
height:20px;
width:20px;
position:absolute;

			background-image: url(data:image/svg+xml;base64,);
}
.comment-footer .col-auto:nth-child(2) a i{
height:20px;
width:20px;
position:absolute;

		background-image: url(data:image/svg+xml;base64,);
}
.comment-footer .col-auto:nth-child(2) a:hover i{
height:20px;
width:20px;
position:absolute;

			background-image: url(data:image/svg+xml;base64,);
}

.comment-footer .col-auto:nth-child(2) a.text-danger i{
height:20px;
width:20px;
position:absolute;

	background-image: url(data:image/svg+xml;base64,);
}

.comment-footer .col-auto:nth-child(3) a i{
height:20px;
width:20px;
position:absolute;

		background-image: url(data:image/svg+xml;base64,);
}
.comment-footer .col-auto:nth-child(3) a:hover i{
height:20px;
width:20px;
position:absolute;

			background-image: url(data:image/svg+xml;base64,);
}
.comment-footer .col-auto:nth-child(4) a i{
height:20px;
width:20px;
position:absolute;

		background-image: url(data:image/svg+xml;base64,);
}
.comment-footer .col-auto:nth-child(4) a:hover i{
height:20px;
width:20px;
position:absolute;

			background-image: url(data:image/svg+xml;base64,);
}

@media (min-width: 576px){
.comment-composer-container form.comment-composer{
margin-left: 0;
margin-right: 0;
}
    
.dropdown-menu.dropdown-menu-right.show .dropdown-item .btn-outline-success.btn.btn-sm{
background: #00b868 !important;
color: white;
width: 110px
}
}
}
