css:
@-moz-document domain("www.reddit.com"), domain("new.reddit.com") {
/*!==================================================================
   1.0 Mod bar
===================================================================== */

/* Background colors */

#tb-bottombar {
    background-color: rgba(26, 26, 27, 0.86)!important;
    border-top: 1px solid rgb(52, 53, 54)!important;
}

.mod-toolbox-rd .tb-modbar-button {
    background-color: rgba(26, 26, 27, 0.86)!important;
    border-left: 1px solid rgb(52, 53, 54)!important;
    border-right: 1px solid rgb(52, 53, 54)!important;
}

#tb-bottombar-hidden {
    background-color: rgba(26, 26, 27, 0.86)!important;
    border-top: 1px solid rgb(52, 53, 54);
    border-right: 1px solid rgb(52, 53, 54);
}

.mod-toolbox-rd #tb-bottombar-hidden {
    background-color: rgba(26, 26, 27, 0.86)!important;
    border-top: 1px solid rgb(52, 53, 54);
    border-right: 1px solid rgb(52, 53, 54);
}

/* Text colors */

#tb-bottombar a {
  color: white;
}

/* Icon colors */

#tb-bottombar > a.tb-toolbar.tb-toolbar-new-settings.tb-icons {
    color: #818384!important;
}

#tb-toolbarcounters {
    color: #818384!important;
}

.mod-toolbox-rd #tb-bottombar-hidden .tb-icons, .mod-toolbox-rd #tb-bottombar .tb-icons {
    color: #818384!important;
}

#tb-toolbarshortcuts > a:before {
    color:#818384!important;
}

/* Modmail hover box */

.mod-toolbox-rd #tb-new-modmail-tooltip,
.mod-toolbox #tb-tooltip{
    background-color: #1a1a1b!important;
    border-color: #030303!important;
    box-shadow: rgba(160, 177, 193, .1) 0px 1px 3px 1px!important;
    color: #b3b5b7;
}

/* Redesign secondary subreddits settings bar */

.mod-toolbox-rd #tb-context-menu {
    background-color: #1a1a1b;
    color: #818384;
    border-color: rgb(52, 53, 54)!important;
}

#tb-config-link,
#tb-un-config-link{
    background-color:#111!important;
    color: #b3b5b7;
    border: 1px solid rgb(0, 0, 0)!important;
}

#tb-config-link:hover,
#tb-un-config-link:hover {
    background-color:#1c1c1c!important;
    color: white;
}

/* my subs */

body > div.tb-popup.subreddits-you-mod-popup > div.tb-popup-header {
    background-color: #030303;
    color: White;
}

body > div.tb-popup.subreddits-you-mod-popup > div.tb-popup-header > div.buttons a {
    color:#b3b5b7!important;
}

body > div.tb-popup.subreddits-you-mod-popup > div.tb-popup-header > div.buttons > a.close {
    background-color:#111;
}

body > div.tb-popup.subreddits-you-mod-popup > div.tb-popup-header > div.buttons > a.close:hover {
    background-color:#1c1c1c;
}

body > div.tb-popup.subreddits-you-mod-popup > div.tb-popup-content {
    background-color: #1a1a1b;
    color: #818384;
}

.mod-toolbox #tb-my-subreddits a {
    color: white;
}

#tb-livefilter-input {
    background-color:#101011;
    color:white
}

body > div.tb-popup.subreddits-you-mod-popup > div.tb-popup-footer {
    background-color: #030303
}

/* Personal Notes */

body > div.tb-popup.personal-notes-popup > div.tb-popup-header { 
    background-color: #030303
}

body > div.tb-popup.personal-notes-popup > div.tb-popup-header > div.tb-popup-title {
    color: White
}

body > div.tb-popup.personal-notes-popup > div.tb-popup-header > div.buttons a {
    color:#b3b5b7!important;
}

body > div.tb-popup.personal-notes-popup > div.tb-popup-header > div.buttons > a.close {
    background-color:#111;
}

body > div.tb-popup.personal-notes-popup > div.tb-popup-header > div.buttons > a.close:hover {
    background-color:#1c1c1c;
}

body > div.tb-popup.personal-notes-popup > div.tb-popup-content, body > div.tb-popup.personal-notes-popup > div.tb-popup-content > table {
    background-color: #1a1a1b;
    color: #818384;
}

#tb-personal-notes-editarea, 
#tb-new-personal-note {
    background-color:#101011!important;
    color:white!important;
}

.mod-toolbox-rd #tb-personal-notes-ul li,
#tb-new-personal-note-div{
    background-color:#111!important;
    color: #b3b5b7;
    border: 1px solid rgb(0, 0, 0);
}

#tb-personal-notes-ul > li > a.tb-personal-note-link {
    color: #b3b5b7;
}

#tb-personal-notes-ul > li.tb-personal-notes-active > a.tb-personal-note-link {
    color: white!important;
    font-weight: bold;
}

/* Bottom Bar */

body > div.tb-popup.personal-notes-popup > div.tb-popup-footer {
    background-color: #030303
}

/*!==================================================================
   2.0 Settings Box
===================================================================== */

/* Background color */

body > div.tb-page-overlay.tb-settings.tb-personal-settings {
    background-color: rgba(28, 28, 28, 0.9);
}

/* Box colors */

body > div.tb-page-overlay.tb-settings.tb-personal-settings > div {
    background-color:#1a1a1b;
    border: 1px solid rgb(52, 53, 54);
}

body > div.tb-page-overlay.tb-settings.tb-personal-settings p,
body > div.tb-page-overlay.tb-settings.tb-personal-settings > div > div.tb-window-tabs-wrapper > div.tb-window-tab.about > div{
    color:#b3b5b7!important;
}

body > div.tb-page-overlay.tb-settings.tb-personal-settings > div input {
    background-color:#101011;
    color:white
}

.mod-toolbox-rd .tb-window-tab.about h3,
.mod-toolbox-rd table.tb-about-credits td{
    border-color: black!important;
}

body.mod-toolbox-rd .tb-action-button {
    background-color: #1a1a1b!important;
    color:#b3b5b7!important;
    border-color: #b3b5b7!important;
}

body.mod-toolbox-rd .tb-action-button:hover {
    background-color: #1a1a1b!important;
    color:#fff!important;
    border-color: #fff!important;
}

.mod-toolbox-rd .tb-window-content p:not([class^="codemirror"]):not(.tb-comment-p) {
    border-color:black
}

/* Header */

body > div.tb-page-overlay.tb-settings.tb-personal-settings > div > div.tb-window-header {
    background-color:#030303;
}

body > div.tb-page-overlay.tb-settings.tb-personal-settings > div > div.tb-window-header > div.tb-window-title {
    color:#b3b5b7;
}

body > div.tb-page-overlay.tb-settings.tb-personal-settings > div > div.tb-window-header > div.buttons > a.tb-help-main {
    background-color:#111;
}

body > div.tb-page-overlay.tb-settings.tb-personal-settings > div > div.tb-window-header > div.buttons > a.tb-help-main:hover {
    background-color:#1c1c1c;
}

body > div.tb-page-overlay.tb-settings.tb-personal-settings > div > div.tb-window-header > div.buttons a {
    color:#b3b5b7!important;
}

body > div.tb-page-overlay.tb-settings.tb-personal-settings > div > div.tb-window-header > div.buttons > a.close {
    background-color:#111;
}

body > div.tb-page-overlay.tb-settings.tb-personal-settings > div > div.tb-window-header > div.buttons > a.close:hover {
    background-color:#1c1c1c;
}

/* Left tab bar */

body > div.tb-page-overlay.tb-settings.tb-personal-settings > div > div.tb-window-tabs a {
    background-color:#111!important;
    color: #b3b5b7;
    border: 1px solid rgb(0, 0, 0);
}

body > div.tb-page-overlay.tb-settings.tb-personal-settings > div > div.tb-window-tabs a:hover {
    background-color:#1c1c1c!important;
    color: white;
}
        
.mod-toolbox-rd .tb-window-wrapper a:not(.tb-comment-author):hover, .mod-toolbox-rd .tb-window-tabs a.active, .mod-toolbox-rd .tb-window-tabs {
    color: white!important;
    border-color: rgb(0, 0, 0);
}

body > div.tb-page-overlay.tb-settings.tb-personal-settings > div > div.tb-window-tabs a.tb-module-disabled {
    background-color:#1a1a1b!important;
    color: #000;
    border: 1px solid rgb(0, 0, 0);
}

/* Bottom Bar */

body > div.tb-page-overlay.tb-settings.tb-personal-settings > div > div.tb-window-footer {
    background-color: black;
}

body > div.tb-page-overlay.tb-settings.tb-personal-settings > div > div.tb-window-tabs-wrapper > div.tb-window-tab > span {
    background-color:#111!important;
    color: white;
    font-weight: bold;
    border: 1px solid rgb(0, 0, 0);
}

/*!==================================================================
   3.0 User note Box
===================================================================== */

/* Header */

body > div.tb-page-overlay.tb-un-editor > div > div.tb-window-header {
    background-color: #030303;
    color: White
}

body > div.tb-page-overlay.tb-un-editor > div > div.tb-window-header > div.buttons a {
    color:#b3b5b7!important;
}

body > div.tb-page-overlay.tb-un-editor > div > div.tb-window-header > div.buttons > a.close {
    background-color:#111;
}

body > div.tb-page-overlay.tb-un-editor > div > div.tb-window-header > div.buttons > a.close:hover {
    background-color:#1c1c1c;
}

/* Box colors */

/* Background color */

body > div.tb-page-overlay.tb-un-editor {
    background-color: rgba(28, 28, 28, 0.9);
}

/* Box colors */

body > div.tb-page-overlay.tb-un-editor > div {
    background-color:#1a1a1b;
    border: 1px solid rgb(52, 53, 54);
}

body > div.tb-page-overlay.tb-un-editor > div > div.tb-window-content {
    color:#b3b5b7!important;
}

body > div.tb-page-overlay.tb-un-editor > div input {
    background-color:#101011;
    color:white
}

body.mod-toolbox-rd .tb-action-button {
    background-color: #1a1a1b!important;
    color:#b3b5b7!important;
    border-color: #b3b5b7!important;
}

body.mod-toolbox-rd .tb-action-button:hover {
    background-color: #1a1a1b!important;
    color:#fff!important;
    border-color: #fff!important;
}

.mod-toolbox-rd .tb-window-content p:not([class^="codemirror"]):not(.tb-comment-p) {
    border-color:black
}

#tb-un-note-content-wrap > div {
    background-color:#1a1a1b;
    border: 1px solid black;
}

#tb-un-note-content-wrap > div > div.tb-un-user-header {
    border-color: rgb(52, 53, 54);
}

#tb-un-note-content-wrap > div.tb-un-info > select {
    background-color: #1a1a1b;
    border-color: rgb(52, 53, 54);
    color:#b3b5b7!important;
}

#tb-un-prune-sb {
    background-color: #030303;
    border-color: rgb(52, 53, 54);
    color:#b3b5b7!important;
}

#tb-un-prune-sb:hover {
    background-color: #1a1a1b;
    border-color: rgb(52, 53, 54);
    color:#b3b5b7!important;
}


/* Bottom Bar */

body > div.tb-page-overlay.tb-un-editor > div > div.tb-window-footer {
    background-color: #030303
}

/*!==================================================================
   4.0 Subreddit Settings
===================================================================== */

/* Background color */

body > div.tb-page-overlay.tb-config {
    background-color: rgba(28, 28, 28, 0.9);
}

/* Header */

body > div.tb-page-overlay.tb-config > div > div.tb-window-header {
    background-color: #030303;
    color: White
}

body > div.tb-page-overlay.tb-config > div > div.tb-window-header > div.buttons a {
    color:#b3b5b7!important;
}

body > div.tb-page-overlay.tb-config > div > div.tb-window-header > div.buttons > a.close {
    background-color:#111;
}

body > div.tb-page-overlay.tb-config > div > div.tb-window-header > div.buttons > a.close:hover {
    background-color:#1c1c1c;
}




/* Box colors */

body > div.tb-page-overlay.tb-config > div {
    background-color:#1a1a1b;
    border: 1px solid rgb(52, 53, 54);
}

body > div.tb-page-overlay.tb-configs p{
    color:#b3b5b7!important;
}

body > div.tb-page-overlay.tb-config > div input {
    background-color:#101011;
    color:white;
}

.mod-toolbox-rd .tb-window-tab.about h3,
.mod-toolbox-rd table.tb-about-credits td{
    border-color: black!important;
}

body.mod-toolbox-rd .tb-action-button {
    background-color: #1a1a1b!important;
    color:#b3b5b7!important;
    border-color: #b3b5b7!important;
}

body.mod-toolbox-rd .tb-action-button:hover {
    background-color: #1a1a1b!important;
    color:#fff!important;
    border-color: #fff!important;
}

.mod-toolbox-rd .tb-window-content p:not([class^="codemirror"]):not(.tb-comment-p) {
    border-color:black
}

body > div.tb-page-overlay.tb-config > div > div.tb-window-tabs-wrapper {
    color: #818384;
}

body > div.tb-page-overlay.tb-config > div > div.tb-window-tabs-wrapper > div.tb-window-tab.settings_home > div > span > a:nth-child(5),
#add-usernote-type,
#tb-config-help,
body > div.tb-page-overlay.tb-config > div > div.tb-window-tabs-wrapper > div.tb-window-tab.removal_reasons_settings > div > table > tbody > tr.advanced-enable > td > a,
#tb-add-removal-reason,
#tb-add-mod-macro{
    background-color: #030303;
    border-color: rgb(52, 53, 54);
    color:#b3b5b7!important;
}

body > div.tb-page-overlay.tb-config > div > div.tb-window-tabs-wrapper > div.tb-window-tab.settings_home > div > span > a:nth-child(5):hover,
#add-usernote-type:hover,
#tb-config-help:hover,
body > div.tb-page-overlay.tb-config > div > div.tb-window-tabs-wrapper > div.tb-window-tab.removal_reasons_settings > div > table > tbody > tr.advanced-enable > td > a:hover,
#tb-add-removal-reason:hover,
#tb-add-mod-macro:hover{
    background-color: #1a1a1b;
    border-color: rgb(52, 53, 54);
    color:#b3b5b7!important;
}

#tb-removal-reasons-list > tr > td.removal-reasons-content > span.removal-reason-edit > textarea,
body > div.tb-page-overlay.tb-config > div > div.tb-window-tabs-wrapper > div.tb-window-tab.removal_reasons_settings > div > table > tbody > tr > td > textarea,
body > div.tb-page-overlay.tb-config > div > div.tb-window-tabs-wrapper > div.tb-window-tab.ban_macro > div > table > tbody > tr > td > textarea {
    background-color:#101011!important;
    color:white!important;
}

.mod-toolbox-rd tr.removal-reason,
.mod-toolbox-rd .edit_removal_reasons tr.removal-reason, .mod-toolbox-rd .edit_mod_macros tr.mod-macro{
    border-color: rgb(52, 53, 54);
}









/* Left tab bar */

body > div.tb-page-overlay.tb-config > div > div.tb-window-tabs a {
    background-color:#111!important;
    color: #b3b5b7!important;
    border: 1px solid rgb(0, 0, 0);
}

body > div.tb-page-overlay.tb-config > div > div.tb-window-tabs a:hover {
    background-color:#1c1c1c!important;
    color: white!important;
}
        
.mod-toolbox-rd .tb-window-wrapper a:not(.tb-comment-author):hover, .mod-toolbox-rd .tb-window-tabs a.active, .mod-toolbox-rd .tb-window-tabs {
    color: white!important;
    border-color: rgb(0, 0, 0);
}

/* Bottom Bar */

body > div.tb-page-overlay.tb-config > div > div.tb-window-footer {
    background-color: #030303
}

/* Scroll Bars */

/* width */
::-webkit-scrollbar {
  width: 20px;
}

/* Track */
::-webkit-scrollbar-track {
  background: #1a1a1a; 
}
 
/* Handle */
::-webkit-scrollbar-thumb {
  background: #0d0d0d; 
  border-radius: 4px 4px 4px 4px;
  border: 1px solid #262626;
}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
  background: #000000; 
  border-radius: 4px 4px 4px 4px;
  border: 1px solid #262626;
}
}
