css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("herokugarden.com"), domain("heroku.com") {


textarea, #sidebar_middle_middle, #main_middle_middle {
  /* Background color for editor window */
  background: #121E31 !important;
}

#editor {
  /* Font for editor window */
  font-family: Monaco, "Courier New", monospace !important;
  /* Color for non-syntax words */
  color: #FFFFFF !important;
}


/* Selected text */
::-moz-selection {
  background: #38566F; /* Firefox */
}

/* Cursor */
#editor #cursor {
  background-color:#FFFFFF !important;
}

/* Ruby Syntax */
.string {
  color: #1DC116!important;
}
.keyword {
  color: #F8BB00 !important;
}
.classname {
  color: #FFFFFF !important;
  text-decoration: underline !important;
}
.symbol {
  color: #B53B3C!important;
}
.blockparams {
  color: #8AA6C1!important;
}
.instancevar {
  color: #ffffff !important;
}
.comment {
  color: #428BDD !important;
  font-style: italic !important;
}
.comment span {
  /* this blocks symbol highlighting within a comment */
  color: #428BDD !important;
  font-style: italic !important;
}

/* ERB Syntax */
.erb_brace {
  color: #077 !important;
}
.erb_ruby {
  color: #6ee !important;
}
.html_tag_brace {
  color: #F8BB00 !important;
}
.html_tag{
  color: #F8BB00!important;
}

/* CSS Syntax */
.css_property_name {
  color: #97965e !important;
}
.css_property_value {
  color: #aaa !important;
}

}
