     background: none !important;
     border-color: #B20C00 !important;
}
.ui-widget-content {
     background: #666;
     color: white;
     border: none !important;
}
.ui-widget-content a {
     transition: .3s;
     color: rgb(200, 200, 200);
     padding: 2px !important;
}
.ui-widget-content a:hover {
     color: white;
