     border: none !important;
}
.dashboardLinkHeaderText {
     transition: 0.3s;
}
a.dashboardItemTitle {
     color: white !important;
     transition: 0.3s;
     padding: 2px !important;
}
.dashboardLinkHeader:hover .dashboardItemTitle {
     /*background: rgba(0, 0, 0, 0.3);*/
     
     background: none !important;
     box-shadow: inset 450px 0px 0px rgba(0, 0, 0, 0.2);
}
