     color: white !important;
}
.iconCol img {
     margin-top: 3px;
}
/* Comm Island */

#s_m_Content_Content_kommIsland_pa .infoCol {
     position: relative;
}
#s_m_Content_Content_kommIsland_pa .infoCol a {
     top: 0;
     padding: 3px 0px;
     transition: 0.3s;
     position: absolute;
     width: 100%;
     padding-left: 1px;
}
#s_m_Content_Content_kommIsland_pa .infoCol:hover a {
     /*background: rgba(0, 0, 0, 0.3) !important;*/
     
