.tooltip {
     background: none !important;
}
#s_m_Content_Content_undervisningIsland_pa .infoCol {
     position: relative;
}
#s_m_Content_Content_undervisningIsland_pa .infoCol span {
     transition: 0.3s;
     position: absolute;
     top: 0;
     width: 100%;
     color: rgb(200, 200, 200) !important;
     padding: 1px 2px;
}
#s_m_Content_Content_undervisningIsland_pa .dashboard .infoCol span:hover {
     /*background: rgba(0, 0, 0, 0.3) !important;*/
     
     box-shadow: inset 350px 0 0 rgba(0, 0, 0, 0.3);
     color: white !important;
}
#s_m_Content_Content_ElevOpgaveAfleveringer .infoCol {
     position: relative;
}
#s_m_Content_Content_ElevOpgaveAfleveringer .infoCol a {
     position: absolute;
     top: 0;
     width: 100%;
     padding: 1px 2px;
     height: 100%;
     color: rgb(200, 200, 200);
     transition: .3s;
}
#s_m_Content_Content_ElevOpgaveAfleveringer .infoCol a:hover {
     background: rgba(0, 0, 0, 0);
     box-shadow: inset 325px 0 0 rgba(0, 0, 0, 0.3);
     color: white;
}
#s_m_Content_Content_ElevOpgaveAfleveringer .infoCol a:hover .homeTaskContainer {
     color: white !important;
}
.homeTaskContainer {
     color: white !important;
}
#s_m_Content_Content_LektierOversigt .infoCol {
     position: relative;
     width: 325px;
}
#s_m_Content_Content_LektierOversigt .infoCol a {
     position: absolute;
     padding: 1px 2px;
     top: 0px;
     height: 100%;
     width: 100%;
     color: rgb(200, 200, 200) !important;
     transition: .3s;
}
#s_m_Content_Content_LektierOversigt .infoCol a:hover {
     /*background: rgba(0, 0, 0, 0.3);*/
     
     background: none;
     box-shadow: inset 325px 0 0 rgba(0, 0, 0, 0.3);
}
#s_m_Content_Content_LektierOversigt .infoCol a:hover .homeTaskContainer {
     color: white !important;
}
.homeTaskContainer {
     color: rgb(200, 200, 200) !important;
}
/* Skema Island */

#s_m_Content_Content_skemaIsland_pa .islandinnerContent h4 {
     background: none !important;
     color: white;
}
#s_m_Content_Content_skemaIsland_pa .islandinnerContent a {
     border: none;
     color: rgb(200, 200, 200);
     transition: .5s;
     background: rgba(0, 0, 0, 0.1);
}
#s_m_Content_Content_skemaIsland_pa .islandinnerContent a div {
     background: none;
}
#s_m_Content_Content_skemaIsland_pa .islandinnerContent a:hover {
     background: rgb(200, 200, 200);
     box-shadow: inset 450px 0 0 rgba(0, 0, 0, 0.3);
}
