     transition: 0.3s;
     background-image: url(https://www.lectio.dk/lectio/img/disc_black.png) !important;
}
#s_m_Content_Content_skemaIsland_pa .islandinnerContent div {
     position: relative;
     height: 30px;
     width: 100%;
}
#s_m_Content_Content_skemaIsland_pa .islandinnerContent div a {
     width: 98% !important;
     height: 25px;
}
#s_m_Content_Content_skemaIsland_pa .s2skemabrikIcons,
#s_m_Content_Content_skemaIsland_pa .s2skemabrikcontent {
     padding-top: 5px;
}
.s2dayHeaderSimple {
     padding-left: 1px;
     padding-bottom: 5px;
}
/* Groups island */

