     color: white;
     background: rgba(0, 0, 0, 0.3);
}
.islandContent {
     padding-top: 5px !important;
}
/* Skema */

#daynote-master .trigger {
     padding-bottom: 5px;
     cursor: pointer;
     position: absolute;
     bottom: 0;
     left: 0;
     text-align: center;
     width: 100%;
     background: -webkit-linear-gradient(top, rgba(248, 248, 248, 0.5), rgba(248, 248, 248, 1));
}
#daynote-master .s2infoHeader {
     padding: 0;
}
#daynote-master .s2infoHeader>div:not(.trigger) {
     position: static !important;
     padding: 0 5px 20px;
}
#daynote-master.collapsed .s2infoHeader>div:not(.trigger) {
     max-height: 15px;
     overflow: hidden;
     -webkit-mask-image: -webkit-linear-gradient(top, rgba(0, 0, 0, 1), rgba(0, 0, 0, 0));
     position: relative;
}
#daynote-master.collapsed .s2infoHeader>div:not(.trigger):after {
     content: '';
     width: 100%;
     height: 100%;
     position: absolute;
     top: 0;
     left: 0;
}
.s2skema .s2infoHeader {
     position: relative;
}
#s_m_Content_Content_SkemaNyMedNavigation_skema_skematabel tbody tr:nth-child(3) .s2skemabrik {
     position: relative;
     word-wrap: break-word;
     left: 0em !important;
}
#s_m_Content_Content_SkemaNyMedNavigation_skema_skematabel tbody tr:nth-child(3) td {
     left: 0em !important;
}
#s_m_Content_Content_SkemaNyMedNavigation_skema_skematabel tbody tr:nth-child(3) td a.s2changed:hover {
     background-image: url(https://www.lectio.dk/lectio/img/disc_white.png) !important;
     background-position: 0em 0.45em !important;
     background-repeat: no-repeat !important;
}
#s_m_Content_Content_SkemaNyMedNavigation_skema_skematabel tbody tr:nth-child(3) td .s2skemabrik:not(.s2changed) {
     background-image: url(https://www.lectio.dk/lectio/img/disc_white.png) !important;
