css:
@-moz-document domain("www.judicialwatch.org") {
body {
    background: #1b1b1b;
    background-color: rgb(27, 27, 27);
    color: #c0c0c0;
}

input {
    background-color: black;
    color: #c0c0c0;
}

.ss_term {
    color: #c0c0c0;
}

#HWI .site_search p {
    background: #303030;
    background-color: rgb(48, 48, 48);
    border: 1px solid black;
    border-top-color: black;
    border-right-color: black;
    border-bottom-color: black;
    border-left-color: black;
}

#HWI .site_search p #s {
    color: #c0c0c0;
}

.withoutsidebar #MW {
    border: 1px solid #000000;
    border-top-color: rgb(0, 0, 0);
    border-right-color: rgb(0, 0, 0);
    border-bottom-color: rgb(0, 0, 0);
    border-left-color: rgb(0, 0, 0);
    background: #303030;
    background-color: rgb(48, 48, 48);
}

body.single-news_links #ContentW h2.CWH2SP, body.single-press_release #ContentW h2.CWH2SP {
    color: #589bce;
}

#TSW #tslinks li a.tsl {
    color: #589bce;
}

#TSWI h1#tsh1, #TSWI h1#tsh1 a {
    color: #c0c0c0;
}

#ContentW div.entrycontent {
    background: #303030;
    background-color: rgb(48, 48, 48);
    color: #c0c0c0;
}

.inlinesidebar #MW .share_toolbox, .withoutsidebar #MW .share_toolbox {
    background: #303030;
    background-color: rgb(48, 48, 48);
}

#NationalW #breadc {
    color: #c0c0c0;
}

#ContentW div.post {
    background-color: #303030;
}

#ContentW div.post h2, #ContentW div.post h2 a {
    color: #589bce;
}

#ContentW h2.CWH2SP, body.single-news_links #ContentW h2.CWH2SP a, body.single-press_release #ContentW h2.CWH2SP a {
    color: #589bce;
}

a {
    color: #589bce;
}

.jw-yt-featured-vid-title {
    color: #589bce;
}

.playlist-item-title {
    font-size: 24px;
    color: #589bce;
}

.detachedsidebar #MW, .inlinesidebar #MW {
    border: 1px solid #000;
    border-top-color: rgb(0, 0, 0);
    border-right-color: rgb(0, 0, 0);
    border-bottom-color: rgb(0, 0, 0);
    border-left-color: rgb(0, 0, 0);
    background: #303030;
    background-color: rgb(48, 48, 48);
}

#ContentW, .inlinesidebar #ContentW {
    background: #303030;
    background-color: rgb(48, 48, 48);
}

.inlinesidebar #ContentW {
    border: 1px solid #303030;
    border-top-color: rgb(48, 48, 48);
    border-right-color: rgb(48, 48, 48);
    border-bottom-color: rgb(48, 48, 48);
    border-left-color: rgb(48, 48, 48);
}

.ph-press-room #ContentW div.post h2, .ph-press-room #ContentW div.post h2 a {
    color: #589bce;
}

.ph-fin-dis .search-box p.searchh2 {
    color: #589bce;
}

.ph-fin-dis h2.title {
    color: #589bce;
}

.ph-fin-dis #fin_disclosures .post .title a {
    color: #c0c0c0;
}

.ph-fin-dis #fin_disclosures .post .date {
    color: #c0c0c0;
}

.ph-fin-dis .search-box form .ss_term {
    background: #000;
    background-color: rgb(0, 0, 0);
    color: #c0c0c0;
}
}
