	background: #1b1b1b;
	border-right: 1px solid #15f275;
}

#toolMenu ul li.is-current a:before, #subSites ul li.is-current a:before {
	border-left-color: #15f275;
	border-right-color: #15f275;
}

#toolMenu ul li.is-current a:hover:before, #subSites ul li.is-current a:hover:before {
	border-left-color: #15f275;
}

#toolMenu ul li a:hover {
	background: #1b1b1b;
}

a[href] {
	color: #6f6f6f !important;
}

