}

nav a:link:hover, nav a:visited:hover {
	color: #15f275 !important;
}


#linkNav ul li.Mrphs-sitesNav__menuitem.is-selected a.link-container  {
	background: #15f275;
}


#toolMenu ul li.is-current a:hover {
	background: #1b1b1b;
	color: #15f275;
}

#toolMenu ul li.is-current a, #subSites ul li.is-current a {
    border-bottom: 1px solid #1b1b1b;
    color: #15f275;
}

#toolMenu ul li a, #subSites ul li a {
	border-bottom: 1px solid #1b1b1b;
}

#toolMenuWrap {
	border-right: 1px solid #15f275;
}

#linkNav ul li.Mrphs-sitesNav__menuitem>.link-container {
	background: #121212;
}

#linkNav ul li.Mrphs-sitesNav__menuitem:not(.dropdown-is-visible) > a:hover, #linkNav ul li.Mrphs-sitesNav__menuitem:not(.dropdown-is-visible) > a:active {
    box-shadow: 0 0 15px 15px rgba(255,255,255,0.0) inset;
}

.Mrphs-toolBody {
	background: #121212 !important;
}

.portletbody {
	background: #121212 !important;
	background-color: #121212 ;
}

th {
	background: #1b1b1b;
}
td {
	background: #121212;
	color: #6f6f6f;
}

body {
	background: #121212;
	background-color: #121212;
}


.portletbody {
	background: #121212;
	background-color: #121212;
}

div.column {
	background-color: #121212;
}
div.column ul li {
	color: #6f6f6f;
}

li {
	color: #6f6f6f;
}

div.col-sm-10 {
	color: #6f6f6f;
}

footer#footer.Mrphs-footer {
	background-color: #1b1b1b;
}

