	color: #121212 !important;
} */

li.Mrphs-sitesNav__menuitem.is-selected a.link-container {
	color: #15f275 !important;
	background-color: transparent !important;
}

#linkNav ul li.Mrphs-sitesNav__menuitem ul {
	border-top: 0px;
}

.Mrphs-sitesNav__submenuitem-title, .Mrphs-sitesNav__submenuitem, .Mrphs-sitesNav__submenuitem-icon {
	background: #1b1b1b !important;
}

li.Mrphs-sitesNav__submenuitem a {
	background: #1b1b1b !important;
}

li.Mrphs-sitesNav__submenuitem a.Mrphs-sitesNav__submenuitem-title, li.Mrphs-sitesNav__submenuitem a.Mrphs-sitesNav__submenuitem-icon {
	color: #6f6f6f !important;
	
}

li.Mrphs-sitesNav__submenuitem:hover a.Mrphs-sitesNav__submenuitem-title, li.Mrphs-sitesNav__submenuitem:hover a.Mrphs-sitesNav__submenuitem-icon {
	color: #15f275 !important;
	text-decoration: none !important;
}


#linkNav ul li.Mrphs-sitesNav__menuitem.dropdown-is-visible.is-selected>a {
	border: 0px;
}

button.Mrphs-toggleNav.js-toggle-nav.btn-link.max {
	background: #1b1b1b;
}

#toolMenu ul li.Mrphs-collapseTools, #subSites ul li.Mrphs-collapseTools {
	background: #1b1b1b;
	border: none;
}

#toolMenu ul li.Mrphs-collapseTools:hover, #subSites ul li.Mrphs-collapseTools:hover {
	background: #1b1b1b;
	border-radius: 0px;
}

.fa-lg, .Mrphs-mainHeader .Mrphs-loginUser li a .login-Icon, #linkNav ul li.Mrphs-sitesNav__menuitem .Mrphs-sitesNav__drop, #linkNav ul li.Mrphs-sitesNav__menuitem .Mrphs-sitesNav__dropdown, .Mrphs-sitesNav__doubledrop, #toolMenu ul li a.is-invisible:after, #toolMenu ul li .Mrphs-roleSwitch__anchor.is-invisible:after, #subSites ul li a.is-invisible:after, #subSites ul li .Mrphs-roleSwitch__anchor.is-invisible:after {
	color: #6f6f6f;
}
.fa-lg:hover {
	color: #15f275;
}

 #toolmenuwrap {
	border-color: #15f275;
}

.Mrphs-toolTitleNav .Mrphs-toolTitleNav__link:hover {
    text-decoration: none;
	background: transparent;
}

.Mrphs-toolTitleNav .Mrphs-toolTitleNav__link {

    display: -webkit-box;
    display: -moz-box;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -webkit-align-items: center;
    align-items: center;
    margin: 5px 5px 5px 0;
    padding: 5px;
    border: 1px solid #6f6f6f;
    border-radius: 3px;
    background: transparent;
    text-decoration: none;
}

.Mrphs-pagebody {
	background: #121212;
}

div#id4 {
	background: #1b1b1b;
	border-color: transparent;
/* 	border-color: #1b1b1b; */
}

table, td, th, tr td, tbody, td {
	background: #121212;
	border-color: #1b1b1b;
}
th:hover  {
	background: #1b1b1b;
}

.tablesorter-bootstrap > tbody > tr.odd > td, .tablesorter-bootstrap > tbody > tr.tablesorter-hasChildRow.odd:hover ~ tr.tablesorter-hasChildRow.odd ~ .tablesorter-childRow.odd > td {

    background-color: #121212;
	color: #15f275;
	border-color: #1b1b1b;

}
.tablesorter-bootstrap > tbody > tr.even > td, .tablesorter-bootstrap > tbody > tr.tablesorter-hasChildRow.odd:hover ~ tr.tablesorter-hasChildRow.odd ~ .tablesorter-childRow.odd > td {

    background-color: #121212;
	color: #15f275;
	border-color: #1b1b1b;
}

.tablesorter-bootstrap > tbody > tr.even:hover > td, .tablesorter-bootstrap > tbody > tr.hover > td, .tablesorter-bootstrap > tbody > tr.odd:hover > td, .tablesorter-bootstrap > tbody > tr.tablesorter-hasChildRow.even:hover ~ .tablesorter-childRow.even > td, .tablesorter-bootstrap > tbody > tr.tablesorter-hasChildRow.odd:hover ~ .tablesorter-childRow.odd > td {

    background-color: #1b1b1b;

}

.tablesorter-bootstrap tfoot td, .tablesorter-bootstrap tfoot th, .tablesorter-bootstrap thead td, .tablesorter-bootstrap thead th {
	background-color: #1b1b1b;
	color: #15f275;
	border-color: #1b1b1b;
}

.gb-summary-category-row td, .gb-summary-category-row th {

    background-color: #121212;
	color: #15f275;
}

.table-bordered > thead > tr > th, .table-bordered > thead > tr > td, .table-bordered > tbody > tr > th, .table-bordered > tbody > tr > td, .table-bordered > tfoot > tr > th, .table-bordered > tfoot > tr > td {

    border: 1px solid #1b1b1b;
        border-top-color: #1b1b1b;
        border-top-style: solid;
        border-top-width: 1px;
        border-bottom-width: 1px;

}

.gb-summary-grade-panel {

    overflow: auto;
    padding: 10px 0;
    margin: 0 0 10px;
    border-bottom: 0px solid #1b1b1b;

}

.table-bordered {

    border: 1px solid #1b1b1b;

}

.table-striped > tbody > tr:nth-of-type(2n+1) {

    background-color: #121212;

}

div.panel.panel-default {
	background-color: #1b1b1b;
	border-color: #1b1b1b;
}

.Mrphs-sakai-announcements .message-body {

    background-color: #121212;
	color: #6f6f6f;
    padding: 15px;
    margin-bottom: 20px;
    border: 1px solid #1b1b1b;
    border-radius: 4px;

}

}
