css:
@-moz-document domain("youtube.com") {
/* {
    /*box-sizing: border-box;
    content: "Subscribe to PewDiePie" !important;
    border-radius: 4px;
    padding: 5px;
    margin: 345px;
    content: " and make sure you're subscribed to PewDiePie" !important;
}*/
h2.ytd-simple-menu-header-renderer:after {
    content: " keeping you subbed to PewDiePie" !important;
}
ytd-multi-page-menu-renderer {
    max-width: 845px !important;
}
ytd-grid-video-renderer #video-title.yt-simple-endpoint.ytd-grid-video-renderer, #title.ytd-shelf-renderer, .yt-simple-endpoint.style-scope.yt-formatted-string, .badge-style-type-live-now.ytd-badge-supported-renderer, .sbqs_c b, #video-title.ytd-video-renderer, #description.ytd-channel-renderer, #message.yt-live-chat-viewer-engagement-message-renderer, #label.ytd-compact-link-renderer, .title.ytd-guide-entry-renderer {
    font-size: 0;
}
#message.yt-live-chat-viewer-engagement-message-renderer:after {
    content: "Welcome to live chat! Remember to guard your privacy, abide by our community guidelines and be subscribed to pewdiepie.";
    font-size: 12px;
}
#search-input.ytd-searchbox-spt input:after, .sbqs_c b:after {
    content: " pewdiepie";
    font-size: 16px;
}
.badge-style-type-live-now.ytd-badge-supported-renderer:after {
    content: "SUB COUNT";
    font-size: 1.2rem;
}
.yt-simple-endpoint.style-scope.yt-formatted-string:after, #description.ytd-channel-renderer:after {
    content: "Subscribe to PewDiePie";
    font-size: 1.3rem;
}
#description-text.ytd-video-renderer:before {
    content: "Subscribe to PewDiePie: youtube.com/pewdiepie ";
    font-size: 1.3rem;
}
.yt-simple-endpoint.style-scope.yt-formatted-string[href="/user/PewDiePie"]:after {
    content: "PewDiePie";
}
ytd-grid-video-renderer #video-title.yt-simple-endpoint.ytd-grid-video-renderer:after, #label.ytd-compact-link-renderer:after, .title.ytd-guide-entry-renderer:after {
    content: "Subscribe to PewDiePie";
    font-size: 1.4rem;
}
.message.ytd-notification-renderer:after {
    content: " and Subscribe to PewDiePie";
    font-size: 1.4rem;
}
#title.ytd-shelf-renderer:after {
    content: "Subscribe to PewDiePie";
    font-size: 1.6rem;
}
#video-title.ytd-video-renderer:after {
    content: "Subscribe to PewDiePie";
    font-size: 1.6rem;
}
paper-item.ytd-guide-entry-renderer {
    padding: 0 16px;
}
}

@-moz-document url("https://www.youtube.com/user/tseries") {
paper-button.ytd-subscribe-button-renderer:before {
    content: "UN";
}
#channel-title.ytd-c4-tabbed-header-renderer {
    font-size: 0;
}
#channel-title.ytd-c4-tabbed-header-renderer:after {
    content: "T-Gay";
    font-size: 2.4rem;
}
/*If you decide to visit T-Series.. lol*/
}
