css:
@-moz-document url-prefix("https://i.sjtu.edu.cn") {
/*基本背景*/
html {
    background-color: transparent;
}
body {
    background-image: url(https://s2.ax1x.com/2019/06/18/VbInyQ.jpg) !important;
    background-repeat: no-repeat !important;
    background-position: center top !important;
    background-size: cover !important;
    background-attachment: fixed !important;
}
html,
body {
    height: 100%;
}

/*未登录时主界面设置*/
.fun-block {
    background: #ffffff40;
}
.log-bxsd {
    box-shadow: 0 0 5px #e0e0e0;
    background: #fff0;
    height: 490px;
}
.sl_log_rt form {
    background-color: rgba(255, 255, 255, 0);
}
/*jaccount登陆提醒栏目隐藏*/
.alert-danger {
    color: #a9444200;
    background-color: #f2dede00;
    border-color: #ebccd100;
}

/*登陆后主界面设置*/
.form-control {    
    background-color: #fff0;
}

/*推荐课表选课界面*/
.sl_all_bg {
    background: #fff0;
}

}
