css:
/* i really want this to be global */
html {
    --fg-cyan: #15b599;
    --fg-white: #FFFFFF;
    --fg-dark: #074238;
    --bg-dark: #1f1f1f;
    --bg-light: #fffdee;
    --bg-green: #cbdfdb;
}

@-moz-document url-prefix("https://forum.mokkels.nl/") {
#header {
    background: url(https://images2.imagebam.com/c7/ae/ba/c405651312820726.jpg) no-repeat;
    background-size: 1200px;
}
body {
    background: var(--bg-dark);
}

#widget_178, #widget_179, #widget_182, #widget_183 {
    display: none;
}

#content, #breadcrumbs, .topic-list {
    background: var(--bg-light) !important;
}
.topic-list, .forum-item {
    background: var(--bg-green) !important;
    color: var(--fg-dark) !important;
}
#topic-tab > table > tbody > tr > td.cell-topic.js-cell-topic > div > a, .forum-item > td.cell-forum > div.forum-wrapper > div > a,div.widget-content > table > tbody > tr > td.cell-topic.js-cell-topic > div > a {
    color: var(--fg-dark) !important;
}
#channel-tabbar, #footer-tabbar {
    background: var(--bg-dark) !important;
}
.channel-tabbar-list > .section-item a {
    background: var(--bg-dark) !important;
}
.channel-tabbar-list > .section-item.current a {
    background: var(--bg-green) !important;
}

.vb-icon, .b-icon {
    filter: hue-rotate(140deg);
}

.b-button, .b-button[disabled], .b-button[disabled]:hover, .b-button--primary, .b-button--primary:link, .b-button--primary:visited, .b-button--primary:focus, .b-button--primary:hover, .b-button--primary-light, .b-button--primary-light:link, .b-button--primary-light:visited, .b-button--secondary, .b-button--secondary:link, .b-button--secondary:visited, .b-button--special, .b-button--special:link, .b-button--special:visited, .fc-button.fc-state-default, .fc-button.fc-state-default.fc-state-disabled, .cke_dialog_body .cke_dialog_contents .b-button, .cke_dialog_body .cke_dialog_contents .b-button:link, .cke_dialog_body .cke_dialog_contents .b-button:visited, .cke_dialog_body .cke_dialog_contents .b-button:focus, .cke_dialog_body .cke_dialog_contents .b-button:hover, .cke_dialog_body .cke_dialog_contents .b-button--primary, .cke_dialog_body .cke_dialog_contents .b-button--primary:link, .cke_dialog_body .cke_dialog_contents .b-button--primary:visited, .cke_dialog_body .cke_dialog_contents .b-button--primary:focus, .cke_dialog_body .cke_dialog_contents .b-button--primary:hover, .cke_dialog_body .cke_dialog_contents .b-button--secondary, .cke_dialog_body .cke_dialog_contents .b-button--secondary:link, .cke_dialog_body .cke_dialog_contents .b-button--secondary:visited, .profile-container .profileTabs .conversation-toolbar-wrapper .conversation-toolbar .b-button.b-button--secondary, .profile-container .profileTabs .conversation-toolbar-wrapper .conversation-toolbar .b-button.b-button--secondary:hover, .profile-container .profileTabs .conversation-toolbar-wrapper .conversation-toolbar a.b-button.b-button--secondary, .profile-container .profileTabs .conversation-toolbar-wrapper .conversation-toolbar a.b-button.b-button--secondary:visited, .profile-container .profileTabs .conversation-toolbar .b-button.b-button--secondary, .profile-container .profileTabs .conversation-toolbar .b-button.b-button--secondary:hover {
    background: var(--bg-green);
}
.b-button--primary, .b-button--primary:link, .b-button--primary:visited, .cke_dialog_body .cke_dialog_contents .b-button--primary, .cke_dialog_body .cke_dialog_contents .b-button--primary:link, .cke_dialog_body .cke_dialog_contents .b-button--primary:visited {
    background: var(--fg-cyan) !important;
    border: var(--fg-cyan) !important;
}
.h-hide-imp {
    display: inline-block !important;
}
.notifications-count {
    background: none;
    border: none;
    color: var(--fg-cyan) !important;
}
.cke_editable {
    background: var(--fg-white) !important;
}
        
        
        
        
        
        
        
        
        
        
        
}

@-moz-document url-prefix("https://www.mokkels.nl/") {
#header {
    background: url(http://images2.imagebam.com/28/61/f4/24d227900000224.jpg) no-repeat;
    height: 287px;
}
#header img {
    display: none;
}
body,
.babeswidget {
    background: var(--bg-dark);
}
.logo a {
    background: url(http://images2.imagebam.com/ba/d4/2f/54585b900000274.png) no-repeat;
}
#nav a,
.comment-form .submit,
.info {

    color: var(--fg-white);
    background: var(--bg-dark) !important;
}



#navbarmkl,
.header-holder {
    background: var(--bg-dark);
}
.add-nav2 li.link,
.add-nav2 a,
.add-nav li a {
    background: none;
}
.tcat h2 a.titleBarMokkels,
#gallery h2 a,
.titleBarMokkels,
.tcat a.titleBarMokkels {
    background: var(--bg-dark);
}
.tcat,
.theadDescription,
td.theadDescription {
    background-color: var(--bg-dark);
}
#sidebar,
#main {
    background: var(--bg-light) !important;
}

#content {
    background: var(--bg-green);
}

#sidebar .block .list span,
#sidebar .block .list .views,
a,
#sidebar .block .list2 span,
span.current,
#content .paging ul a:hover {
    color: var(--fg-cyan);
}

#content .block .date {
    color: var(--fg-dark);
}

#sidebar > div:nth-child(6),
#content .paging ul.list li:last-child,
.choice-form {
    display: none;
}

#content .paging ul li,
#content .paging .mark,
#sidebar .block .area h2 {
    background: none;
}
}
