css:
@-moz-document domain("ffxiah.com") {
body
  {
    background-color: #0f0e11!important;
  }

  #mw-page-base
  {
    background-color: #0f0e11!important;
    background-image: none!important;
  }

  div#localNotice td
  {
    background: #0f0e11!important;
    color: #FFFFFF!important;
  }

div#siteNotice table tr td {
    border: 1px solid #a9d3ff;
}

div#siteNotice table tr td table tr td {
    border: 0!important;
}

  table
  {
    background: #0f0e11!important;
    color: #FFFFFF!important;
    border: 2px#FFFFFF!important;
  }

  p
  {
    background: #0f0e11!important;
    color: #FFFFFF!important;
  }

  div
  {
    background: #0f0e11!important;
    color: #FFFFFF!important;
  }

  td
  {
    background: #0f0e11!important;
    color: #FFFFFF!important;
    border: 1px solid hsla(0,0%,100%,.05);
  }

  tr
  {
    background: #0f0e11!important;
    color: #FFFFFF!important;
    border: 1px solid #FFFFFF!important;
  }

  a
  {
    color: #6441a4!important;
  }

  a:visited
  {
    color: #b19dd8!important;
  }

  ul li a
  {
    background: #0f0e11!important;
    color: #6441a4!important;
  }

  ul li a:visited
  {
    background: #0f0e11!important;
    color: #b19dd8!important;
  }

  h1
  {
    color: #FFFFFF!important;
    font-weight: 500!important;
  }

  h2
  {
    color: #FFFFFF!important;
    font-weight: 500!important;
  }

  h3
  {
    color: #FFFFFF!important;
    font-weight: 500!important;
  }

  h4
  {
    color: #FFFFFF!important;
    font-weight: 500!important;
  }

  h5
  {
    color: #FFFFFF!important;
    font-weight: 500!important;
  }

  th
  {
    background: #19171c!important;
    color: #FFFFFF!important;
  }

  a span
  {
    color: #6441a4!important;
  }

  td span
  {
    color: #6441a4!important;
  }

option {
    color: #6441a4!important;
}

#server-select {
    color: #6441a4!important;
}

#sideMenu .sideMenuitem a:hover {
    background-color:#b19dd8!important;
    color:white!important;
}

a:hover {
    background-color:#b19dd8!important;
    color:white!important;
}
}
