css:
@-moz-document domain("moonquill.com") {
/*GENERIC*/
 body {
    background: #999;
}

/*HEADER*/
.navbar {
    background: #666;
}
.navbar .img-fluid{
    background: #999;border-radius:50%;
}
.navbar .nav-link {
    color: #fff !important;
}
.navbar .nav-link:hover, .navbar .nav-link:active {
    color: #6666ff !important;
}
#book, .navbar .input-group-text {
    background:#999;
}
.navbar-brand {
    background: #999;
    border-radius:20px;
}

/*RIGHT PANNEL*/
.modal-body {
    background:#999;color:#000;
}
.modal-footer {
    background:#999;color:#000;border-radius:0px;
}
.modal-body .close {
    color:#fff;
}
.modal-body a {
    color:#fff;
}
.modal-body a:hover, .modal-body a:active {
    background:#666;color:#fff;
}
.text-muted{color:#fff !important;}

/*CONTENT PAGE*/
 .main-content {
    background: #999;
}
.main-content .card, .container .card {
    background: #888;
    border:#000 1px solid;
}

/* -home- */
.main-content .card .table-responsive th {
    background-color: #666;
    color: #999;
}
.main-content .card .table-responsive td a, .main-content .card a .mt-3 {
    color: #ccc;
}
.main-content .card .table-responsive td a:hover, .main-content .card .table-responsive td a:active, .main-content .card a:hover .mt-3, .main-content .card a:active .mt-3 {
    color: #385672;
}
.main-content .card .badge {
    color: #fff;
    background-color: #666;
}
.main-content .card .card-title {
    color: #3856AA;
}

/* -nav-tab- */
.nav-tabs {
    border-top:#000 1px solid;
    border-left:#000 1px solid;
    border-bottom:0;
    margin-top:10px;
}
.nav-tabs .nav-item {
    margin:0;
}
.nav-tabs a.nav-link {
    background-color: #888;
    margin:0;
    padding:10px;
    border-right:#000 1px solid;
    border-bottom:#000 1px solid;
    color:#000 !important;
}
.nav-tabs a.nav-link:not(.active):hover, .nav-tabs a.nav-link:not(.active):active {
    border-right:#000 1px solid;
    border-bottom:#000 1px solid;
    color:#fff !important;
}
.nav-tabs a.nav-link.active {
    background-color: #666;
    border-bottom:#000 1px solid;
    border-right:#000 1px solid;
    color:#fff !important;
}
.header-body {
    border:0;
}
/* -explorer- */
.header-body .btn-outline-secondary{ background: #888; color: #ccc; cursor:pointer; }
.header-body .btn-outline-secondary *{ cursor:pointer; }
.header-body .btn-outline-secondary:hover, .header-body .btn-outline-secondary:active{ background: #6E84A3; color: #fff; }

/* -library- */
.header-img-top2{ filter: invert(100%) contrast(20%) sepia(100%) invert(100%); }
 h6.header-pretitle {
    color: #555;
}
.card .card-body .card-title {
    color:#444;
}
.card .card-body .card-title a:hover, .card .card-body .card-title a:active {
    color:#bbb;
}
.card .card-body .progress-bar {
    background-color: #000066;
}
}
