css:
@-moz-document domain('owa.hpi.de'){
  :root{
      --main-bg-color: #1e1e1e;
      --sec-bg-color: #252526;
      --sec-hl-color: #474747;
      --main-fg-color: #d4d4d4;
      --main-hl-color: #3794ff;
      --nav-bar-bg-color: #333333;
      background-color: var(--main-bg-color);
  }

  body{
      background-color: var(--main-bg-color);
      color: var(--main-fg-color);
      /*scrollbar-color: var(--sec-hl-color) var(--main-bg-color);*/
  }

  a{
     color: var(--main-hl-color);
  }

  #preloadDiv, #owaLoading{
      background-color: var(--main-bg-color);
  }

  /*top nav bar*/
  .o365cs-base.o365cst.o365cs-topnavBGColor-2{
      background-color: var(--nav-bar-bg-color);
  }

  .o365cs-base.o365cst .o365cs-topnavLinkBackground-2{
      background-color: var(--nav-bar-bg-color);
  }

  .o365cs-base.o365cst .o365cs-topnavText, .o365cs-base.o365cst .o365cs-topnavText:hover{
      color: var(--main-fg-color);
  }

  /*menu button*/
  .o365cs-base.o365cst .o365cs-navMenuButton{
      color: var(--nav-bar-bg-color);
  }

  /*menu button*/
  .ms-bg-color-themePrimary, .ms-bgc-tp, .ms-bg-color-themePrimary-hover:hover, .ms-bg-color-themePrimary-focus:focus, .ms-bg-color-themePrimary-before::before, .ms-bgc-tp-h:hover, .ms-bgc-tp-f:focus, .ms-bgc-tp-b::before{
      background-color: var(--main-hl-color);
  }

  .o365cs-nav-header16 .o365cs-nav-o365Branding, .o365cs-nav-header16 .o365cs-nav-rightAlign .o365cs-nav-button{
      border-left-width: 0;
  }

  .listItemDefaultBackground{
      background-color: var(--main-bg-color);
  }

  .ms-font-l, .ms-font-s, .ms-font-color-neutralSecondary, .ms-fontColor-neutralSecondary, .ms-fcl-ns, .ms-font-color-neutralSecondary-hover:hover, .ms-font-color-neutralSecondary-focus:focus, .ms-font-color-neutralSecondary-before::before, .ms-fcl-ns-h:hover, .ms-fcl-ns-f:focus, .ms-fcl-ns-b::before{
      color: var(--main-fg-color);
  }

  .ms-bg-color-themeLight, .ms-bgc-tl, .ms-bg-color-themeLight-hover:hover, .ms-bg-color-themeLight-focus:focus, .ms-bg-color-themeLight-before::before, .ms-bgc-tl-h:hover, .ms-bgc-tl-f:focus, .ms-bgc-tl-b::before{
      background-color: var(--sec-hl-color);
  }

  .ms-bg-color-neutralLight, .ms-bgc-nl, .ms-bg-color-neutralLight-hover:hover, .ms-bg-color-neutralLight-focus:focus, .ms-bg-color-neutralLight-before::before, .ms-bgc-nl-h:hover, .ms-bgc-nl-f:focus, .ms-bgc-nl-b::before{
      background-color: var(--sec-hl-color);
  }

  .ms-font-color-neutralDark, .ms-fontColor-neutralDark, .ms-fcl-nd, .ms-font-color-neutralDark-hover:hover, .ms-font-color-neutralDark-focus:focus, .ms-font-color-neutralDark-before::before, .ms-fcl-nd-h:hover, .ms-fcl-nd-f:focus, .ms-fcl-nd-b::before{
      color: var(--main-fg-color);
  }

  .ms-font-xl{
      color: var(--main-fg-color);
  }

  .ms-font-m{
      color: var(--main-fg-color);
  }

  .ms-bg-color-white, .ms-bgc-w, .ms-bg-color-white-hover:hover, .ms-bg-color-white-focus:focus, .ms-bg-color-white-before::before, .ms-bgc-w-h:hover, .ms-bgc-w-b::before {
      background-color: var(--main-bg-color);
  }

  html[dir] hr._qc_B{
      border-color: var(--sec-hl-color);
  }

  .ms-font-color-themePrimary, .ms-fontColor-themePrimary, .ms-fontColor-themePrimary, .ms-fcl-tp, .ms-font-color-themePrimary-hover:hover, .ms-font-color-themePrimary-focus:focus, .ms-font-color-themePrimary-before::before, .ms-fcl-tp-h:hover, .ms-fcl-tp-f:focus, .ms-fcl-tp-b::before{
      color: var(--main-hl-color);
  }

  ._n_d1, ._n_e1{
      border-left-color: var(--sec-hl-color);
  }

  ._n_r1{
      border-left-color: var(--sec-hl-color);
  }

  .ms-border-color-neutralLight, .ms-bcl-nl, .ms-border-color-neutralLight-hover:hover, .ms-border-color-neutralLight-focus:focus, .ms-border-color-neutralLight-before::before, .ms-bcl-nl-h:hover, .ms-bcl-nl-f:focus, .ms-bcl-nl-b::before{
      border-color: var(--sec-hl-color);
  }

  /*top bar*/
  .ms-bg-color-themeLighterAlt, .ms-bgc-tlra, .ms-bg-color-themeLighterAlt-hover:hover, .ms-bg-color-themeLighterAlt-focus:focus, .ms-bg-color-themeLighterAlt-before::before, .ms-bgc-tlra-h:hover, .ms-bgc-tlra-f:focus, .ms-bgc-tlra-b::before{
      background-color: var(--sec-bg-color);
  }

  .ms-font-color-neutralPrimary, .ms-fontColor-neutralPrimary, .ms-fcl-np, .ms-font-color-neutralPrimary-hover:hover, .ms-font-color-neutralPrimary-focus:focus, .ms-font-color-neutralPrimary-before::before, .ms-fcl-np-h:hover, .ms-fcl-np-f:focus, .ms-fcl-np-b::before{
      color: var(--main-fg-color);
  }

  /*sidebar*/
  .ms-bg-color-neutralLighter, .ms-bgc-nlr, .ms-bg-color-neutralLighter-hover:hover, .ms-bg-color-neutralLighter-focus:focus, .ms-bg-color-neutralLighter-before::before, .ms-bgc-nlr-h:hover, .ms-bgc-nlr-f:focus, .ms-bgc-nlr-b::before{
      background-color: var(--sec-bg-color);
  }
  /*search bar*/
  .ms-bg-color-themeLighter, .ms-bgc-tlr, .ms-bg-color-themeLighter-hover:hover, .ms-bg-color-themeLighter-focus:focus, .ms-bg-color-themeLighter-before::before, .ms-bgc-tlr-h:hover, .ms-bgc-tlr-f:focus, .ms-bgc-tlr-b::before{
      background-color: var(--main-bg-color);
  }
}
