css:
@-moz-document domain("moi3d.com") {
:root {
	--bg: #222;
	--txt: #aaa;
	--light: #fff3;
}

/* hide */
hr,
[class^="msgframe_m"],
.msgframe_topcenter > table > tbody > tr > td[width="1%"]:first-child,
.postresponse td[width="25%"]:first-child,
._postresponse td > img,
[align="center"] table[width="98%"] > tbody > tr:first-child,
_ {
	display: none;
}

/* common */
body {
	background: var(--bg);
	color: var(--txt);
}

a, a:hover {
	color: #36b;
}

a.threadname, a.threadname:hover {
	color: var(--txt);
}

[class^="msgframe"] {
	background: var(--bg);
}

br + br {
	font-size: .7rem;
	line-height: 1.4;
}

/* header */
#navbordercell {
	padding-top: 15px;
}
#navborder,
.navdivider,
.navpage a, 
_ {
	border: none;
	background: none;
	color: var(--txt);
}
#imgcell>img {
  filter: brightness(1.2);
}
img[width="290"],
#navdiv1>img,
_ {
	display: none;
}

.navpage {
	_background: var(--bg);
}
select#cdadaafbcecadcfdd {
	opacity: .5;
	margin: 8px 10px;
}

/* sidebar */
.thread_left_img,
img[name^="t"] {
	display: none;
}
.thread_left_img {
	padding: 0 !important;
}
.eventhread,
.oddthread {
	background: none;
  border: none;
	line-height: 1.1;
	padding: 5px 7px;
}

/* message header */
.msgframe_topcenter,
.postnumber,
.postnumber a,
.posttofrom a,
_ {
	color: var(--light) !important;
	text-decoration: none;
}
.postnumber:hover a,
.posttofrom:hover a,
_ {
	color: var(--txt) !important;
	text-decoration: underline !important;	
}
.msgframe_topcenter td,
._msgframe_bottomcenter td,
_ {
	display: inline;
}
.msgframe_topcenter td {
	font-size: .6rem;
}
.msgframe_topcenter > table:first-child {
	width: auto !important;
}
.postnumber {
	position: relative;
	text-align: right;
	top: -20px;
}

/* message text */
br + div[align="center"] {
	padding-top: 1rem;
}

body, .msgbody {
	font-family: Georgia;
	font-size: 1.3rem;
	line-height: 1.4;
	padding: 5px 10px;
}

body.navpage,
body.threadlistbody,
_ {
	font-family: Roboto;
	font-size: 9pt;
	padding: 0;
	background: var(--bg);
}

body:not([onload]) p,
body:not([onload]) p b {
	_color: var(--light);
  font-weight: normal !important;
}

.msgbody,
.msgframe_bottomcenter,
.msgframe_bottomcenter > table > tbody > tr:nth-of-type(3) > td,
.msgframe_topcenter,
_ {
	padding: 0;
}
.msgbody {
  position: relative;
	top: -20px !important;
}
.posttofrom {
	font-size: 1rem;
}
.sig, .sig *, .sig a {
	color: var(--light);
	font-size: .8rem;
	font-style: italic;
	text-align: left;
}
.sig br+br {
	font-size: 0;
}
.sig p {
	padding: 0;
	margin: 0;
}
.edit_text {
	margin: 0;
	color: var(--light);
}

/* message buttons */
.postresponse,
.postresponse td,
_ {
	display: inline;
	border: none;
	text-align: left;
}
.postresponse img {
	opacity: .3;
}
.postnumber a,
.postresponse a {
	color: var(--light);
	text-decoration: none;
}
.post_options_menu a {
	color: var(--txt);
}
.postresponse a:hover {
	color: var(--txt);
	text-decoration: underline;	
}

/* message submenu */
.box {
	border: none;
}
.post_options_container_opened {
	box-shadow: 2px 2px 10px var(--light);
	border-radius: 6px;
	padding: 7px 10px;
  position: relative;
	left: 65px !important;
  top: 10px !important;
}
.subhead {
	color: var(--txt);
}
.post_options_container_opened .box td,
.posthead td[align="center"],
.post_options_menu,
_ {
	padding: 0;
}

/* code */
pre, pre.code {
	background: #0001 !important;
	border: solid 1px #0002;
	font-family: "PragmataPro Mono";
	font-size: .8rem;
	padding: 5px 10px;
}
}
