css:
@-moz-document url-prefix("https://app.zenhub.com/workspaces/") {
.zhc-issue-card__connected-pull-requests .zhc-label {
    margin: 0;
}

.zhc-issue-card__connected-pull-requests .zhc-issue-card__meta {
    padding: 0;
}

.zhc-issue-card__connected-pull-requests .zhc-issue-card__issue-title {
    display: none;
}

.zhc-issue-card__meta__badges .zhc-label {
    font-size: .6em;
}

.zhc-issue-card__connected-pull-request .zhc-issue-card__header {
    padding: 0;
}

.zhc-icon--issue-closed-red {
    background-image: url("http://www.pngmart.com/files/3/Green-Tick-PNG-Pic.png");
}

.zhc-issue-card__meta .zhc-issue-card__section__meta, .zhc-pipeline-header__story-points {
    display: none;
}

.zhc-issue-card__epics .zhc-issue-card__section__meta {
    display: none;
}

.zhc-issue-card__epics .zhc-issue-card__section__content {
    text-align: center;
}

.zhc-issue-card__epics{
    border-top: 1px solid #ccc;
    margin:0;
    padding:0;
}

.zhc-issue-card__epics > .zhc-issue-card__section{
    margin-top:2px;
    padding:2px;
    font-size: 10px;
    font-weight:bold;
    background-color:#5e60ba;
    color: #fff;
}

.zhc-icon.zhc-icon--epic-line.zhc-icon--grey-light{
    display:none;
}

.zhc-btn.zhc-btn--tight.zhc-issue-card__epic-filter__btn.zhc-btn--primary-bordered{
 font-size: 10px;
}

.zhc-pipeline--add-placeholder {
    display:none;
}

.zhc-issue-card__repo-name{
    font-weight: bold;
    font-size:12px;
}

.zhc-issue-card__issue-title{
    font-size:12px;
}

.zhc-pipeline, .zhc-pipeline-header {
	background-color:#ebebeb;
	color: #6c6c6c;
}

.zhc-icon--blocked-dark {
    background-image: url("data:image/svg+xml;charset=utf-8,%3Csvg width='16' height='16' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd' stroke='red' stroke-width='1.4'%3E%3Cpath d='M10.59 1.814l-5.16.045-3.616 3.68.045 5.16 3.68 3.618 5.16-.045 3.618-3.681-.045-5.16-3.681-3.617z'/%3E%3Cpath d='M11.598 4.364L3.7 12.474' stroke-linecap='square'/%3E%3C/g%3E%3C/svg%3E");
}

.zhc-icon--warning-outline-dark {
    background-image: url("data:image/svg+xml;charset=utf-8,%3Csvg width='16' height='16' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='%23333'%3E%3Cpath d='M7 6.043h2v4H7zM7 11.043h2v2H7z'/%3E%3C/g%3E%3Cpath d='M8.262 1.136a.3.3 0 0 0-.533-.001L.783 14.46a.3.3 0 0 0 .266.439H14.86a.3.3 0 0 0 .267-.437L8.262 1.136z' stroke='orange' stroke-width='1.4'/%3E%3C/g%3E%3C/svg%3E");
}

.zhc-menu-bar-item--estimate-filter {
    display: none;
}

.zhc-menu-bar-item--release-filter {
    display: none;
}

.zhc-menu-bar-item--milestone-filter {
    display: none;
}
}
