@-moz-document domain('mail.google.com') {
body, td, tr, input, textarea, select {
    font-family: 'open sans', 'source sans pro', 'helvetica neue', sans-serif;
}

.gt {
    font-size: 90%;
    line-height: 1.5em;
    font-weight: 400;
    color: #222;
}

.LW-avf {
    font-size: 0.95em;
    line-height: 1.45em;
}

/* //////// GLOBAL NON-COLOR ///////// */

.Bu.y3, .aKB, .aKl {
    display: none;
}

.aAy {
    width: auto;
}

/* opacity icons */

.gbqfi, .gb_X .gb_Nb .gb_Pb, .aQS, .n4 .G-asx, .aii, .gb_Pb, .T-I-auR.ayk-axZ-aup-I .ayk-axZ-aup-auo, .aqN, .aos, .gb_Qb {
    opacity: .7;
    background-color: transparent;
}

.gb_Rb {
    opacity: 0.7;
}

.l6 {
    padding-top: 0;
}

.J-KU-Jg-K9 .J-KU .aAy, .aRy, .aKj {
    height: 46px;
}

.zA {
    height: 32px;
}

body, td, tr, input, textarea, select {
    font-family: "Helvetica Neue", arial;
}

.aKj {
    padding-top: 4px;
}

/* search text*/

.gbqfif, .gbqfq {
    height: auto;
    font-family: "Helvetica Neue", arial;
    margin: 0;
}

/* tab icons, something, labels */

.aKp, .aRx, .as {
    display: none;
}

/* //////// BACKGROUND COLOR ///////// */

.wl {
    background: #171A21;
    /* background: linear-gradient(45deg, #042029 0%,#073642 146%); */
}

/* //////// TRANSLUSCENT WHITE ///////// */

.aOd, .aJ6 {
    color: rgba(255,255,255,.7);
}

.aAy.aAy.aIf-aLe.J-KU-Je.J-KU-JW .aKo {
    background-color: rgba(255,255,255,.7);
}

.J-KU-Jg-K9.aAA .aAy, .J-KU-Jg-K9.aAA .J-KU-KO.aAy, .aKj, .yO, .xY, .aKz, .gbqfqw, #gbqfb, .UI .TB {
    border: none;
    background: transparent;
    color: rgba(255,255,255,.7);
    box-shadow: none;
}

/* search bar */

.gbqfqw {
    border-bottom: 1px dashed rgba(255,255,255,.3);
    /*
    border-bottom: 1px dashed rgba(0,0,0,.3);
    */
}

/* //////// TRANSLUSCENT BLACK ///////// */

.NQ {
    background-color: rgba(0,0,0,.3);
}


/* //////// SEARCH TEXT COLOR ///////// */

.gbqfif {
    color: #F573C7;
}

/* //////// UNREAD HIGHLIGHT LINE ///////// */

.zE {
    background: #292936;
}

/* //////// EMAIL FROM ///////// */

.xY, .e, .x2, .CL .CK {
    color: #8DBBE0;
}

/* //////// EMAIL TITLE ///////// */

.y6 span {
    color: #7A8CBE;
}

/* //////// GRAY VARIANT 1 ///////// */

.TO .n0, .akh, .mj, .l8, .l9, .ma, .B .Di .Dj, .l6, .z .Di .Dj, .Wz .Wy {
    color: #878C91;
}

/* buttons */

.T-I-KE, .G-atb .T-I-ax7, .TD, .aOd {
    color: #878C91;
    background-color: rgba(0,0,0,.35);
    background-image: none;
    border: none;
}

/* //////// GRAY VARIANT 2 ///////// */

.y6 .y2, .h0, .C .Di .Dj {
    color: #878C91;
}

/* //////// SIDEBAR MORE LINK ///////// */

.n4 {
    color: #F573C7;
}

