.ain {
    border-color: #F573C7;
}

/* //////// HOUHIN ///////// */

.bX a, .Cz.J-J5-Ji.e {
    color: #B587DE;
}

/* contact header */

.bX {
    color: #B587DE;
    background-color: rgba(0,0,0,.35);
}

/* //////// SENCHA ///////// */

.T-I-auR {
    background-image: none;
    border: none;
    background-color: #B587DE;
    color: rgba(255,255,255,.8);
}

/* Promotions line */

.aJi-aLe .aDG, .aAy.J-KU-JW.aAy.aJi-aLe .aKo, .aAy.J-KU-KO.aAy.aJi-aLe .aKo {
    background-color: #B587DE;
}

/* //////// SHENG ///////// */

