.b8 .vh, .cc .vh, .vX .vh {
    background-color: #FFBA66;
    border: none;
    color: rgba(0,0,0,.85);
}

/* Updates Line */

.aH2-aLe .aDG, .aAy.J-KU-JW.aAy.aH2-aLe .aKo, .aAy.J-KU-KO.aAy.aH2-aLe .aKo {
    background-color: #DEF085;
}

/* //////// GAIWAN ///////// */

/* tooltips */

.T-ays, .T-ays-atD, .T-ays-atC {
    background-color: #878C91;
    border: none;
    color: rgba(0,0,0,.85);
}

/* //////// KYUSU ///////// */

.aIf-aLe .aDG, .aAy.J-KU-JW.aAy.aIf-aLe .aKo, .aAy.J-KU-KO.aAy.aIf-aLe .aKo {
    background-color: #FFBA66;
}

/* did you mean */

.Wz, .ya {
    color: #FFBA66;
    background-color: rgba(0,0,0,.35);
    padding: 11px;
}

/* email line cursor */

.PF.xY.PE {
    background: #FFBA66;
}

/* tab text */

.aKz {
    color: #FFBA66;
}
}
