css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("hordes.fr") {
/* v1.0 */
#hmupdater {
display:none;
position:fixed;
right:5px;
bottom:5px;
z-index:1000;
border:2px solid black;
}

.hmu\:class\:box {
border:1px solid #DDAB76;
padding:5px 10px;
color:inherit;
background-color:#5c2b20;
}

#hmupdater strong {
display:block;
width:8.5em;
text-align:center;
cursor:pointer;
color:#f0d79e;
}

#hmupdater strong:hover {
color:#c88f60;
}


#hmu\:coords {
display:none;
position:absolute;
z-index:2;
width:260px;
margin-top:-40px;
margin-left:5px;
padding:4px;
font-size:8pt;
background-color:#3B3249;
border:1px solid #AFACC1;
outline:2px solid black;
}

#hmu\:coords p {
background-color:#696486;
margin-bottom:0;
padding:0 3px;
text-align:left;
}

#hmu\:coords .field {
display:block;
margin:15px auto;
}

#hmu\:coords .button {
width:125px;float:left;text-align:center;
}

#hmu\:coords .button + .button {
float:right;
}

#hmu\:link * {
vertical-align:middle;
}
}
