css:
@-moz-document url-prefix("https://akshatmittal.com/youtube-realtime") {
.card-block {
    background: #25272a;
    color: #c0c0c0;
}

.col-12 {
    background: #333;
}

.footer {
    color: #c0c0c0;
    background: #333;
}

.col-sm-12 {
    background: #333;
}

.row {
    background: #333;
}


h1, h2, h3, h4, h5, h6 {
    color: #fff;
}

.sidebar-nav {
    background: #34373c;
}

.preloader {
    background: #333;
}

.form-control {
    color: #c0c0c0;
    background-color: #333 !important;
}

[role="button"], a, area, button, input, label, select, summary, textarea {
    color: #c0c0c0 !important;
}

.card-no-border .card {

    border-color: #222;
    box-shadow: 0 5px 20px #222;
}

realtime.af77184dec69e96e69aff958ae2bb738 div {
    border: 2px solid #222;
        border-top-color: rgb(34, 34, 34);
        border-top-style: solid;
        border-top-width: 2px;
    background: #333;
    color: #c0c0c0;
}

.dark .post-actions {
    background: #34373c;
}

.dark .textarea {
    border-color: #222;
    border-radius: 4px;
    background: #333;
    color: #c0c0c0;
}

.textarea-wrapper--top-level .textarea-wrapper {
    background: #34373c;
}

.textarea-wrapper {
    border: 2px solid #222;
}

.post-actions {
    border-top: solid 2px #222;
}

.input--text:placeholder-shown {
    color: #c0c0c0;
    background: #333 !important;
    border: 2px solid #222 !important;
}

.page-wrapper {
    background: #333;
}

.left-sidebar {
    background: #34373c;
    box-shadow: 1px 0 20px #34373c;
}

.col-3 {
    background: #27252a;
}

.col-6 {
    background: #27252a;
}

a {
    color: #fff !important;
}

.footer:after {
    content: ' | YouTube Realtime Dark v1.0.1 by sks316' !important;
    color: #c0c0c0;
}
}
